INSERT INTO `d_video_lesson` (`ID`, `NAME_RUS`, `NAME_KAZ`, `NAME_LAN`, `LINK`, `DESC_RUS`, `DESC_KAZ`, `DESC_LAN`, `D_LESSON_ID`) VALUES
('9112302b-7e04-48d8-b439-d3417be1faff', 'Урок 1.1 Видеоролик и диалог', 'Сабақ 1.1 Бейне және диалог', 'Sabaq 1.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/wPrBzIq3iPs', '<ul>\n	<li>- Девушка, вы не скажете, где находится музей?</li>\n	<li>- Какой музей?</li>\n	<li>- Центральный музей.</li>\n	<li>- Центральный музей находится на улице Ауэзова.</li>\n	<li>- Большое спасибо.</li>\n	<li>____________________________________________</li>\n	<li>&nbsp;</li>\n	<li>- Скажите, где находится гостиница?</li>\n	<li>- А название гостиницы?</li>\n	<li>- Гостиница &laquo;Арай&raquo;.</li>\n	<li>- Он находится возле воказала, идите направо.</li>\n	<li>- Спасибо.</li>\n</ul>\n', '<ul>\n	<li>- Қарындас, айтып жібересіз бе, мұражай қай жерде?</li>\n	<li>- Қандай мұражай?</li>\n	<li>- Орталық мұражай.</li>\n	<li>- Орталық мұражай Әуезов көшесінде.</li>\n	<li>- Көп рақмет!</li>\n	<li>____________________________________________</li>\n	<li>&nbsp;</li>\n	<li>&nbsp;</li>\n	<li>- Кешіріңіз, айтып жіберіңізші, қонақүй қай жақта?</li>\n	<li>- Қай қонақүй?</li>\n	<li>- &laquo;Арай&raquo; қонақүйі.</li>\n	<li>- Ол вокзалдың жанында, оңға қарай жүріңіз.</li>\n	<li>- Рақмет.</li>\n</ul>\n', '<p>- qaryndas, ajtyp zhiberesiz be, murazhaj qaj zherde?<br />\n- qandaj murazhaj?<br />\n- ortalyq murazhaj.<br />\n- ortalyq murazhaj aeuezov koeshesinde.<br />\n- koep raqmet!<br />\n____________________________________________<br />\n- keshiringiz, ajtyp zhiberingizshi, qonaquej qaj zhaqta?<br />\n- qaj qonaquej?<br />\n- &laquo;araj&raquo; qonaqueji.<br />\n- ol vokzaldyng zhanynda, onggha qaraj zhueringiz.<br />\n- raqmet.</p>\n', '9d358b6c-67a1-49c4-a30c-899e1d2fd1ad'),
('bd54ba48-0ea8-4b13-b52f-9c4241bcbe0f', 'Урок 2.1 Видеоролик и диалог', 'Сабақ 2.1 Бейне және диалог', 'Sabaq 2.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/_WqknRR2lzk', '<ul>\n	<li>Айша: - Здравствуй, Газиза! Не узнала?</li>\n	<li>Газиза: - Здравствуй, Айша! Давно не виделись. Как твои дела?</li>\n	<li>Айша: - Слава богу, неплохо. А как у тебя дела?</li>\n	<li>Газиза: - Спасибо, хорошо. Айша, ты ничуть не изменилась! Где сейчас живешь?</li>\n	<li>Айша: - Сейчас мы живем здесь, в Алматы. Супруга зовут Куаныш. Сын ходит в садик, дочка в школу.</li>\n	<li>А ты как?</li>\n	<li>Газиза: - У меня тоже все хорошо, живу в Таразе. Работаю в поликлинике.</li>\n	<li>Айша: - А куда сейчас идешь? Пошли к нам, гостем будешь.</li>\n	<li>Ғазиза: - Спасибо за внимание, Айша. Извини, сейчас я тороплюсь на семинар. Потом позвоню.</li>\n	<li>Айша: - Хорошо, до встречи</li>\n	<li>Газиза: - До свидания.</li>\n</ul>\n', '<ul>\n	<li>Айша: - Сәламатсың ба, Ғазиза! Танымай қалдың ба?</li>\n	<li>Ғазиза: - Сәламат па, Айша! Көрмегелі көп болды. Жағдайың қалай?</li>\n	<li>Айша: - Шүкір, жаман емес. Өзің қалайсың?</li>\n	<li>Ғазиза: - Рахмет, жақсы. Айша, сен түк өзгермепсің! Қазір қайда тұрасың?</li>\n	<li>Айша: - Біз қазір осында, Алматыда тұрып жатырмыз. Жолдасымның аты &ndash; Қуаныш. Ұлым балабақшаға, қызым мектепке барып жүр. Ал, сен ше?</li>\n	<li>Ғазиза: - Менде бәрі жақсы, шүкір. Тараз қаласында тұрамын. Емханада жұмыс жасаймын.</li>\n	<li>Айша: - Қазір қайда бара жатырсың? Үйге жүр, қонақ бол.</li>\n	<li>Ғазиза: - Рақмет, ниетіне. Мен қазір асығып бара жатырмын семинарға. Кейін телефон соғамын.</li>\n	<li>Айша: - Жақсы, кездескенше!</li>\n	<li>Ғазиза: - Сау бол.</li>\n</ul>\n', '<p>ajsha: - saelamatsyng ba, ghazijza! tanymaj qaldyng ba?<br />\nghazijza: - saelamat pa, ajsha! koermegeli koep boldy. zhaghdajyng qalaj?<br />\najsha: - shuekir, zhaman emes. oezing qalajsyng?<br />\nghazijza: - rahmet, zhaqsy. ajsha, sen tuek oezgermepsing! qazir qajda turasyng?<br />\najsha: - biz qazir osynda, almatyda turyp zhatyrmyz. zholdasymnyng aty &ndash; quanysh. ulym balabaqshagha, qyzym mektepke baryp zhuer. al, sen she?<br />\nghazijza: - mende baeri zhaqsy, shuekir. taraz qalasynda turamyn. emhanada zhumys zhasajmyn.<br />\najsha: - qazir qajda bara zhatyrsyng? uejge zhuer, qonaq bol.<br />\nghazijza: - raqmet, nijetine. men qazir asyghyp bara zhatyrmyn semijnargha. kejin telefon soghamyn.<br />\najsha: - zhaqsy, kezdeskenshe!<br />\nghazijza: - sau bol.</p>\n', '2d9045f4-e6f6-48a2-8f4d-f2137f0a4efb'),
('99226cc5-a457-4154-9e5f-5897d59c144f', 'Урок 3.1 Видеоролик и диалог', 'Сабақ 3.1 Бейне және диалог', 'Sabaq 3.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/7qhIBOFalHs', '<ul>\n	<li>Улжан: &ndash; Юля, вы опоздали сегодня на работу?</li>\n	<li>Юля:&ndash; Да, опоздала на работу.</li>\n	<li>Улжан: &ndash; Вы на автобусе приехали?</li>\n	<li>Юля: &ndash; Нет, сегодня приехала на такси. Поздно вышла.</li>\n	<li>Улжан: &ndash; А я рано вышла, все равно опоздала. Наш автобус так ездит.</li>\n	<li>Юля:&ndash; Ұлжан, и домой на нашем автобусе ездишь?</li>\n	<li>Улжан: &ndash; По-разному, иногда на автобусе, а иногда на машине Асылбека. А вы?</li>\n	<li>Юля:&ndash; Я люблю ходить пешком.</li>\n	<li>Улжан: - Хорошо.</li>\n</ul>\n', '<ul>\n	<li>Ұлжан: &ndash; Юля, сіз бүгін жұмысқа кешіктіңіз бе?</li>\n	<li>Юля:&ndash; Иә, жұмысқа кешігіп келдім.</li>\n	<li>Ұлжан: &ndash; Автобуспен келдіңіз бе?</li>\n	<li>Юля:&ndash; Жоқ, бүгін таксимен келдім. Кеш шықтым.</li>\n	<li>Ұлжан:&ndash; Ал мен ерте шықсам да, бәрібір кешіктім. Біздің автобус солай жүреді.</li>\n	<li>Юля: &ndash; Ұлжан, үйге де біздің автобуспен қайтасың ба?</li>\n	<li>Ұлжан:&ndash; Әрқалай, кейде автобуспен, кейде Асылбектің машинасымен барамын. Сіз ше?</li>\n	<li>Юля:&ndash; Мен жаяу жүргенді ұнатамын.</li>\n	<li>Ұлжан: - Жақсы.</li>\n</ul>\n', '<p>ulzhan: &ndash; julja, siz buegin zhumysqa keshiktingiz be?<br />\njulja:&ndash; ijae, zhumysqa keshigip keldim.<br />\nulzhan: &ndash; avtobuspen keldingiz be?<br />\njulja:&ndash; zhoq, buegin taksijmen keldim. kesh shyqtym.<br />\nulzhan:&ndash; al men erte shyqsam da, baeribir keshiktim. bizding avtobus solaj zhueredi.<br />\njulja: &ndash; ulzhan, uejge de bizding avtobuspen qajtasyng ba?<br />\nulzhan:&ndash; aerqalaj, kejde avtobuspen, kejde asylbekting mashijnasymen baramyn. siz she?<br />\njulja:&ndash; men zhajau zhuergendi unatamyn.<br />\nulzhan: - zhaqsy.</p>\n', 'a4a38449-ade4-45fb-ba46-e6099b749f6b'),
('63df9030-0f54-4836-adc8-a2c1999e6e94', 'Урок 4.1 Видеоролик и диалог', 'Сабақ 4.1 Бейне және диалог', 'Sabaq 4.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/VgFa40d3uhs', '<ul>\n	<li>Раушан: Маржан, посмотри на это красное платье, какое красивое!</li>\n	<li>Маржан: Цвет слишком яркий. Нет ли других цветов?</li>\n	<li>Раушан: Вот, есть зеленый цвет. Мне очень нравятся желтые, зеленые цвета.</li>\n	<li>Маржан: Тогда давай купим. Я куплю вот эту голубую блузку.</li>\n	<li>Раушан: Блузка очень идет тебе, будешь носить с черной юбкой.</li>\n	<li>Маржан: А эта красная сумка подойдет к твоему платью.</li>\n	<li>Раушан: Качество хорошее, можно взять. Мне нравится сумка и платье. Я их куплю.</li>\n	<li>Маржан: С обновой тебя, носи на здоровье.</li>\n	<li>Раушан: Спасибо!</li>\n</ul>\n', '<ul>\n	<li>Раушан: Маржан, мына қызыл көйлекті қарашы, өте әдемі!</li>\n	<li>Маржан: Түсі тым ашық қой. Басқа түстері жоқ па?</li>\n	<li>Раушан: Міне, жасыл түсі бар. Маған сары, жасыл түстер өте ұнайды.</li>\n	<li>Маржан: Ендеше алайық. Маған мына көк блузка ұнайды, мен осыны сатып аламын.</li>\n	<li>Раушан: Блузка саған жарасып тұр, қара юбкамен киерсің.</li>\n	<li>Маржан: Мына қызыл сөмкені қарашы, көйлегіңе үйлеседі екен.</li>\n	<li>Раушан: Сапасы жақсы, сатып алуға болады. Көйлекті де, сөмкені де сатып аламын.</li>\n	<li>Маржан: Құтты болсын, игілігіңе ки!</li>\n	<li>Раушан: Рақмет!</li>\n</ul>\n', '<p>raushan: marzhan, myna qyzyl koejlekti qarashy, oete aedemi!<br />\nmarzhan: tuesi tym ashyq qoj. basqa tuesteri zhoq pa?<br />\nraushan: mine, zhasyl tuesi bar. maghan sary, zhasyl tuester oete unajdy.<br />\nmarzhan: endeshe alajyq. maghan myna koek bluzka unajdy, men osyny satyp alamyn.<br />\nraushan: bluzka saghan zharasyp tur, qara jubkamen kijersing.<br />\nmarzhan: myna qyzyl soemkeni qarashy, koejleginge uejlesedi eken.<br />\nraushan: sapasy zhaqsy, satyp alugha bolady. koejlekti de, soemkeni de satyp alamyn.<br />\nmarzhan: qutty bolsyn, ijgiliginge kij!<br />\nraushan: raqmet!</p>\n', '64968d2f-4951-4e42-b821-271cc06590df'),
('119ef2d9-dd37-44bd-815c-b506727ebf41', 'Урок 5.1 Видеоролик и диалог', 'Сабақ 5.1 Бейне және диалог', 'Sabaq 5.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/WpeC9xkLBjI', '<ul>\n	<li>Айжан: Фархат, это ты на этой фотографии, в середине?</li>\n	<li>Фархат: Как ты меня узнал, на этой фотографии мне 6 лет.</li>\n	<li>Айжан: А кто это справа от тебя?</li>\n	<li>Фархат: Это мой брат, сейчас он с семьей живет в Караганде. Рядом с матерью сестры &ndash; Зухра и Фатима.</li>\n	<li>Айжан: А этот маленький мальчик Шухрат наверное?</li>\n	<li>Фархат: Да, это мой братишка Шухрат, самый младший в семье.</li>\n	<li>Айжан: Где он сейчас? Женился?</li>\n	<li>Фархат: Нет, пока холост. Летом женится.</li>\n	<li>Айжан: О, значит, летом нас ждет большой той.</li>\n	<li>Фархат: Конечно, пригласим обязательно.</li>\n</ul>\n', '<ul>\n	<li>Айжан: Фархат, мына суретте ортадағы сен бе?</li>\n	<li>Фархат: Қалай танып қойдың, бұл жерде мен алты жастамын.</li>\n	<li>Айжан: Оң жағыңда тұрған кім?</li>\n	<li>Фархат: Бұл ағам, қазір отбасымен бірге Қарағандыда тұрады. Анамның қасында әпкелерім &ndash; Зухра мен Фатима.</li>\n	<li>Айжан: Ал мына кішкентай бала Шухрат па?</li>\n	<li>Фархат: Иә, ол біздің үйдің кенжесі.</li>\n	<li>Айжан: Ол қазір қайда? Үйленді ме?</li>\n	<li>Фархат: Жоқ, әзірше бойдақ. Жазда үйленеді.</li>\n	<li>Айжан: О, жазда үлкен той болады екен ғой... Шақырасындар ма?</li>\n	<li>Фархат: Әрине, міндетті түрде шақырамын.</li>\n</ul>\n', '<p>ajzhan: farhat, myna surette ortadaghy sen be?<br />\nfarhat: qalaj tanyp qojdyng, bul zherde men alty zhastamyn.<br />\najzhan: ong zhaghyngda turghan kim?<br />\nfarhat: bul agham, qazir otbasymen birge qaraghandyda turady. anamnyng qasynda aepkelerim &ndash; zuhra men fatijma.<br />\najzhan: al myna kishkentaj bala shuhrat pa?<br />\nfarhat: ijae, ol bizding uejding kenzhesi.<br />\najzhan: ol qazir qajda? uejlendi me?<br />\nfarhat: zhoq, aezirshe bojdaq. zhazda uejlenedi.<br />\najzhan: o, zhazda uelken toj bolady eken ghoj... shaqyrasyndar ma?<br />\nfarhat: aerijne, mindetti tuerde shaqyramyn.</p>\n', 'd2db173f-f8cc-4a4b-b7e6-7dad9788bbb9'),
('0f6d9ff8-1e68-46c7-b15e-2f2b4849f96e', 'Урок 1.1 Видеоролик и диалог', 'Сабақ 1.1 Бейне және диалог', 'Sabaq 1.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/ZuVCC_4KLNI', '<ul>\n	<li>-</li>\n	<li>Жанар: - Алия, когда ты нас познакомишь со своим парнем? Какой он из себя?</li>\n	<li>Алия:- Он &ndash; высокий, стройный парень.</li>\n	<li>Жанар: - Это тот усатый парень, который стоял рядом с тобой на новогодней фотографии?</li>\n	<li>Алия:- У него нет усов, волосы кудрявые, носит очки.</li>\n	<li>Жанар:- Какой у него характер, открытый?</li>\n	<li>Алия:- Асхат &ndash; открытый, добрый парень. Он умный и трудолюбивый.</li>\n	<li>Жанар: - Ты его не перехвалила?</li>\n	<li>Алия: - Он мне нравится. И мы любим друг &ndash; друга.</li>\n	<li>Жанар: - Подруга, какая ты счастливая! А тот усатый парень, который стоит рядом с тобой, кто он?</li>\n	<li>Алия:- Он &ndash; друг Асхата.</li>\n	<li>Жанар: - Познакомь меня с ним.</li>\n	<li>Алия: - Ладно, в субботу я познакомлю тебя с Асхатом и его другом...</li>\n</ul>\n', '<ul>\n	<li>ОЛ ӨЗІ ҚАНДАЙ ЖІГІТ?</li>\n	<li>Жанар: - Әлия, бізді жігітіңмен қашан таныстырасың? Ол өзі қандай жігіт?</li>\n	<li>Әлия:- Ұзын бойлы, сымбатты жігіт қой.</li>\n	<li>Жанар:- Жаңа жылдағы суретте қасыңда тұрған мұртты жігіт пе?</li>\n	<li>Әлия: - Жоқ, оның мұрты жоқ, шашы бұйра, көзілдірік киетін жігіт.</li>\n	<li>Жанар: - Ал, мінезі қандай, ашық па?</li>\n	<li>Әлия: - Асхаттың мінезі ашық, ақкөңіл, мейірімді жігіт. Өзі әрі ақылды, әрі еңбекқор жігіт.</li>\n	<li>Жанар:- Оны тым мақтап жіберген жоқсың ба?</li>\n	<li>Әлия: - Ол маған ұнайды. Біз бір-бірімізді жақсы көреміз.</li>\n	<li>Жанар: - Құрбым, сен қандай бақыттысың! Ал суреттегі сенің қасыңда тұрған мұртты жігіт кім?</li>\n	<li>Әлия: - Ол Асхаттың досы ғой.</li>\n	<li>Жанар: - Мені таныстыршы.</li>\n	<li>Әлия:- Жақсы, сенбі күні өзіңді Асхатпен де, оның досымен де таныстырайын...</li>\n</ul>\n', '<p>ol oezi qandaj zhigit?<br />\nzhanar: - aelijja, bizdi zhigitingmen qashan tanystyrasyng? ol oezi qandaj zhigit?<br />\naelijja:- uzyn bojly, symbatty zhigit qoj.<br />\nzhanar:- zhanga zhyldaghy surette qasyngda turghan murtty zhigit pe?<br />\naelijja: - zhoq, onyng murty zhoq, shashy bujra, koezildirik kijetin zhigit.<br />\nzhanar: - al, minezi qandaj, ashyq pa?<br />\naelijja: - ashattyng minezi ashyq, aqkoengil, mejirimdi zhigit. oezi aeri aqyldy, aeri engbekqor zhigit.<br />\nzhanar:- ony tym maqtap zhibergen zhoqsyng ba?<br />\naelijja: - ol maghan unajdy. biz bir-birimizdi zhaqsy koeremiz.<br />\nzhanar: - qurbym, sen qandaj baqyttysyng! al surettegi sening qasyngda turghan murtty zhigit kim?<br />\naelijja: - ol ashattyng dosy ghoj.<br />\nzhanar: - meni tanystyrshy.<br />\naelijja:- zhaqsy, senbi kueni oezingdi ashatpen de, onyng dosymen de tanystyrajyn...</p>\n', 'c1efeb1c-d411-4a2b-aac2-acec71f329ea'),
('6571cfd9-6fd3-45c1-9f09-2d8afffc7c14', 'Урок 2.1 Видеоролик и диалог', 'Сабақ 2.1 Бейне және диалог', 'Sabaq 2.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/pLGg8UJZJG0', '<ul>\n	<li>ЧТО СКАЗАЛ (А) БАКЫТ?</li>\n	<li>- Бакыт, сегодня мне позвонил Акылбек, он приглашает нас в субботу в гости.</li>\n	<li>- По какому поводу?</li>\n	<li>- Приехал его сын, который учился в Германии.</li>\n	<li>- Старший сын учился в Германии, а младший учится в Турции. И младший тоже приехал?</li>\n	<li>- Нет, младший сын, оказывается, еще не приехал.</li>\n	<li>- У Акылбека дети способные, учатся зарубежом, по гранту. Хорошая семья. Надо бы обязательно их навестить и разделить радость. Однако и Сауле тоже пригласила нас на субботу. Что же делать?</li>\n	<li>- Сауле ведь пригласила на обед, а к Акылбеку пойдем вечером.</li>\n	<li>- Хорошо, так и сделаем.</li>\n</ul>\n', '<ul>\n	<li>БАҚЫТ НЕ ДЕДІ?</li>\n	<li>- Бақыт, маған бүгін Ақылбек хабарласты, сенбіге үйіне қонаққа шақырды.</li>\n	<li>- Қандай себеппен шақырып жатыр екен?</li>\n	<li>- Алманияда оқитын баласы бүгін оқуын бітіріп келіпті.</li>\n	<li>- Үлкен баласы Алманияда, кіші ұлы Түркияда оқып жатыр еді ғой. Кіші ұлы да келіп пе?</li>\n	<li>- Жоқ, кенжесі әлі келмепті.</li>\n	<li>- Ақылбектің балалары өте зерек, шетелде грантпен оқып жүр. Отбасы да жақсы. Міндетті түрде барып, қуаныштарын бөлісу керек. Бірақ сенбіге Сәуле де үйіне шақырып еді, қайтсек екен?</li>\n	<li>- Сәуле түскі асқа шақырды ғой, ал Ақылбектің үйіне кешке барамыз.</li>\n	<li>- Мақұл, солай істейік.</li>\n</ul>\n', '<p>baqyt ne dedi?<br />\n- baqyt, maghan buegin aqylbek habarlasty, senbige uejine qonaqqa shaqyrdy.<br />\n- qandaj sebeppen shaqyryp zhatyr eken?<br />\n- almanijjada oqijtyn balasy buegin oquyn bitirip kelipti.<br />\n- uelken balasy almanijjada, kishi uly tuerkijjada oqyp zhatyr edi ghoj. kishi uly da kelip pe?<br />\n- zhoq, kenzhesi aeli kelmepti.<br />\n- aqylbekting balalary oete zerek, shetelde grantpen oqyp zhuer. otbasy da zhaqsy. mindetti tuerde baryp, quanyshtaryn boelisu kerek. biraq senbige saeule de uejine shaqyryp edi, qajtsek eken?<br />\n- saeule tueski asqa shaqyrdy ghoj, al aqylbekting uejine keshke baramyz.<br />\n- maqul, solaj istejik.</p>\n', '770ac5b9-4db4-447a-99b5-fa89be643b22'),
('d239eb5b-7282-4dcf-82c6-cb7274668c99', 'Урок 3.1 Видеоролик и диалог', 'Сабақ 3.1 Бейне және диалог', 'Sabaq 3.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/o2cF7Plq8AQ', '<ul>\n	<li>-</li>\n	<li>- Доброе утро, Айгуль Аскаровна!</li>\n	<li>- Здравствуйте, Айдос. Вчера вы были на собрании?</li>\n	<li>- Да, я был. А вы не смогли присутствовать, да?</li>\n	<li>- Да, у меня были другие неотложные дела. О чем говорили на собрании?</li>\n	<li>- Шеф сказал, что нужно быстрее заканчивать подготовку итогового отчета. Руководитель информационно-аналитического отдела попросил, чтобы дали еще недельный срок , но шеф не согласился.</li>\n	<li>- Почему?</li>\n	<li>- Поступило указание министерства о том, чтобы мы сдали документ до конца месяца.</li>\n	<li>- Что сказал начальник отдела кадров?</li>\n	<li>- Ничего не говорил, он молчал.</li>\n	<li>- В таком случае, работаем и в субботу, и в воскресенье, да?</li>\n	<li>- Да, придется работать на выходные...</li>\n</ul>\n', '<ul>\n	<li>ЖИНАЛЫСТА БОЛДЫҢЫЗ БА?</li>\n	<li>- Айгүл Асқарқызы, қайырлы таң!</li>\n	<li>- Сәламатсыз ба, кеше жиналыста болдыңыз ба?</li>\n	<li>- Иә, жиналыста болдым. Сіз қатыса алмадыңыз ғой деймін.</li>\n	<li>- Менің тығыз шаруаларым болып қалды. Жиналыста не айтты?</li>\n	<li>- Бастық &laquo;қорытынды есепті тез бітіру керек&raquo; деді. Ақпараттық-талдау бөлімінің жетекшісі &laquo;тағы да бір апта уақыт беріңіз&raquo; деп айтып көріп еді, бастық көнбеді.</li>\n	<li>- Неге?</li>\n	<li>- Министрлік &laquo;айдың аяғына дейін тапсырыңдар&raquo; деген нұсқау беріпті.</li>\n	<li>- Кадрлар бөлімінің бастығы не деді?</li>\n	<li>- Ол ештеңе демеді, үндемей отырды.</li>\n	<li>- Демек, сенбі мен жексенбі күндері де жұмыс істейтін болдық қой.</li>\n	<li>- Иә, демалыста да жұмыс істеуге тура келеді...</li>\n</ul>\n', '<p>zhijnalysta boldyngyz ba?<br />\n- ajguel asqarqyzy, qajyrly tang!<br />\n- saelamatsyz ba, keshe zhijnalysta boldyngyz ba?<br />\n- ijae, zhijnalysta boldym. siz qatysa almadyngyz ghoj dejmin.<br />\n- mening tyghyz sharualarym bolyp qaldy. zhijnalysta ne ajtty?<br />\n- bastyq &laquo;qorytyndy esepti tez bitiru kerek&raquo; dedi. aqparattyq-taldau boelimining zhetekshisi &laquo;taghy da bir apta uaqyt beringiz&raquo; dep ajtyp koerip edi, bastyq koenbedi.<br />\n- nege?<br />\n- mijnijstrlik &laquo;ajdyng ajaghyna dejin tapsyryngdar&raquo; degen nusqau beripti.<br />\n- kadrlar boelimining bastyghy ne dedi?<br />\n- ol eshtenge demedi, uendemej otyrdy.<br />\n- demek, senbi men zheksenbi kuenderi de zhumys istejtin boldyq qoj.<br />\n- ijae, demalysta da zhumys isteuge tura keledi...</p>\n', '96ec387d-4d5d-4742-bfd4-23f844497604'),
('c0366b5f-5f09-4650-bf44-de367c40b2b4', 'Урок 4.1 Видеоролик и диалог', 'Сабақ 4.1 Бейне және диалог', 'Sabaq 4.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/m6yVXWuzpsI', '<ul>\n	<li>-</li>\n	<li>Максат: - Привет, Жазира. Как дела?</li>\n	<li>Жазира: - Здравствуй, Максат. Как сам? Все хорошо?</li>\n	<li>Максат: - Неплохо. В прошлом году, после вашей свадьбы, я уехал учиться в Малайзию. Вернулся недавно, сейчас работаю здесь в строительной компании. Вы сами как?</li>\n	<li>Жазира: - У нас маленький ребенок. Я нахожусь в декретном отпуске, а Галым работает с раннего утра до позднего вечера. Ему приходится работать за двоих.</li>\n	<li>Максат: - Конечно, так и должно быть... Поздравляю Вас с рождением ребенка! Мальчик или девочка?</li>\n	<li>Жазира: - Нашу девочку зовут Айсауле. Она еще маленькая, но уже узнает нас, улыбается. Приходи к нам в гости - сам все увидишь.</li>\n	<li>Максат: - Конечно, обязательно приду. Передавай привет Галыму.</li>\n	<li>Жазира: - До свидания, Максат.</li>\n</ul>\n', '<ul>\n	<li>ЖҰМЫС ІСТЕУГЕ ТУРА КЕЛЕДІ.</li>\n	<li>Мақсат: - Сәлем, Жазира. Қалың қалай?</li>\n	<li>Жазира: - Сәлемет пе, Мақсат. Өзің қалайсың? Бәрі жақсы ма?</li>\n	<li>Мақсат: - Жаман емес. Былтыр сендердің үйлену тойларыңнан кейін Малайзияға оқуға кеткенмін. Жақында келдім, осында құрылыс компаниясында жұмыс істеп жатырмын. Өздерің қалайсыңдар?</li>\n	<li>Жазира: - Біз кішкентайлы болдық. Өзім декреттік демалыстамын. Ғалым таңертеңнен қара кешке дейін жұмыс істейді. Оған екі адамның орнына жұмыс істеуге тура келеді.</li>\n	<li>Мақсат: - Әрине, солай болуы тиіс. Кішкентайлы болуларыңмен құттықтаймын! Ұл ма, қыз ба?</li>\n	<li>Жазира: - Қызымыздың аты &ndash; Айсәуле. Кішкентай болса да бізді таниды, бізге қарап күледі. Үйге қонаққа келсеңші, өзің бәрін көресің.</li>\n	<li>Мақсат: - Әрине, міндетті түрде келемін. Ғалымға сәлем айт.</li>\n	<li>Жазира: - Сау бол, Мақсат.</li>\n</ul>\n', '<p>zhumys isteuge tura keledi.<br />\nmaqsat: - saelem, zhazijra. qalyng qalaj?<br />\nzhazijra: - saelemet pe, maqsat. oezing qalajsyng? baeri zhaqsy ma?<br />\nmaqsat: - zhaman emes. byltyr senderding uejlenu tojlaryngnan kejin malajzijjagha oqugha ketkenmin. zhaqynda keldim, osynda qurylys kompanijjasynda zhumys istep zhatyrmyn. oezdering qalajsyngdar?<br />\nzhazijra: - biz kishkentajly boldyq. oezim dekrettik demalystamyn. ghalym tangertengnen qara keshke dejin zhumys istejdi. oghan eki adamnyng ornyna zhumys isteuge tura keledi.<br />\nmaqsat: - aerijne, solaj boluy tijis. kishkentajly bolularyngmen quttyqtajmyn! ul ma, qyz ba?<br />\nzhazijra: - qyzymyzdyng aty &ndash; ajsaeule. kishkentaj bolsa da bizdi tanijdy, bizge qarap kueledi. uejge qonaqqa kelsengshi, oezing baerin koeresing.<br />\nmaqsat: - aerijne, mindetti tuerde kelemin. ghalymgha saelem ajt.<br />\nzhazijra: - sau bol, maqsat.</p>\n', 'f56518b8-8b05-494c-a927-90be9a9db7a4'),
('70b74729-1067-4297-874d-58a187c32821', 'Урок 5.1 Видеоролик и диалог', 'Сабақ 5.1 Бейне және диалог', 'Sabaq 5.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/4CN_VEiYUUo', '<ul>\n	<li>-</li>\n	<li>Сания:- Здравствуйте, Куаныш Турсынулы!</li>\n	<li>Куаныш Турсынулы: - Здравствуй, Сания!</li>\n	<li>Сания : - Простите за беспокойство, у меня есть просьба к Вам.</li>\n	<li>Куаныш Турсынулы: - Какая просьба, Сания ?</li>\n	<li>Сания : - Я хотела у Вас отпроситься на две недели.</li>\n	<li>Куаныш Турсынулы: - Хотите куда-то поехать?</li>\n	<li>Сания: - Я хотела съездить в Москву.</li>\n	<li>&nbsp;</li>\n	<li>Куаныш Турсынулы:- В Москву?!</li>\n	<li>Сания : - Да, в Москву. Куаныш Турсынулы, Вы же знакомы с Нурланом. Он сейчас учится в Москве. Я хотела с ним встретиться.</li>\n	<li>Куаныш Турсынулы: - Значит, хотите встретиться с Нурланом? Все понятно. Надо подумать.</li>\n	<li>Сания : - Куаныш Турсынулы, если вы разрешите, Куралай заменит меня на должности вашего секретаря.</li>\n	<li>Куаныш Турсынулы: - Сания, я Вас отпускаю. Но я могу разрешить только на 10 дней, пишите заявление.</li>\n	<li>Сания: - Мне хватит и десяти дней. Большое Вам спасибо!</li>\n</ul>\n', '<ul>\n	<li>СІЗДЕН ЕКІ АПТАҒА СҰРАНАЙЫН ДЕП ЕДІМ.</li>\n	<li>Сәния:- Сәлеметсіз бе, Қуаныш Тұрсынұлы!</li>\n	<li>Қуаныш Тұрсынұлы:- Сәлемет пе, Сәния!</li>\n	<li>Сәния:- Мазалағаным үшін кешіріңіз, сізге бір өтініш айтайын деп едім.</li>\n	<li>Қуаныш Тұрсынұлы:- Ол қандай өтініш, Сәния?</li>\n	<li>Сәния:- Мен сізден екі аптаға сұранайын деп едім.</li>\n	<li>Қуаныш Тұрсынұлы:- Бір жаққа барасыз ба?</li>\n	<li>Сәния:- Мен Мәскеуге барғалы жатырмын.</li>\n	<li>Қуаныш Тұрсынұлы:- Мәскеуге?!</li>\n	<li>Сәния:- Иә, Мәскеуге. Сіз Нұрланды танисыз ғой. Ол қазір Мәскеуде оқып жатыр. Мен онымен кездесейін деп едім.</li>\n	<li>Қуаныш Тұрсынұлы:- Иә, Нұрланмен кездесем деңіз? Бәрі түсінікті. Ойланып көрейік.</li>\n	<li>Сәния:- Қуаныш Тұрсынұлы, егер рұқсат етсеңіз, Құралай менің орнымда сіздің хатшыңыз бола алады.</li>\n	<li>Қуаныш Тұрсынұлы:- Сәния, сізді жіберемін. Бірақ, 10 күнге ғана... Онда да өтініш жазып кетсеңіз...</li>\n	<li>Сәния:- Он күн де жетіп қалады. Сізге үлкен рахмет, Қуаныш Тұрсынұлы!</li>\n</ul>\n', '<p>sizden eki aptagha suranajyn dep edim.<br />\nsaenijja:- saelemetsiz be, quanysh tursynuly!<br />\nquanysh tursynuly:- saelemet pe, saenijja!<br />\nsaenijja:- mazalaghanym ueshin keshiringiz, sizge bir oetinish ajtajyn dep edim.<br />\nquanysh tursynuly:- ol qandaj oetinish, saenijja?<br />\nsaenijja:- men sizden eki aptagha suranajyn dep edim.<br />\nquanysh tursynuly:- bir zhaqqa barasyz ba?<br />\nsaenijja:- men maeskeuge barghaly zhatyrmyn.<br />\nquanysh tursynuly:- maeskeuge?!<br />\nsaenijja:- ijae, maeskeuge. siz nurlandy tanijsyz ghoj. ol qazir maeskeude oqyp zhatyr. men onymen kezdesejin dep edim.<br />\nquanysh tursynuly:- ijae, nurlanmen kezdesem dengiz? baeri tuesinikti. ojlanyp koerejik.<br />\nsaenijja:- quanysh tursynuly, eger ruqsat etsengiz, quralaj mening ornymda sizding hatshyngyz bola alady.<br />\nquanysh tursynuly:- saenijja, sizdi zhiberemin. biraq, 10 kuenge ghana... onda da oetinish zhazyp ketsengiz...<br />\nsaenijja:- on kuen de zhetip qalady. sizge uelken rahmet, quanysh tursynuly!</p>\n', 'bcc62564-f0a7-413a-a562-44ca09938ada'),
('2e1c1f12-2944-4867-9929-66a4f24d78d4', 'Урок 1.1 Видеоролик и диалог', 'Сабақ 1.1 Бейне және диалог', 'Sabaq 1.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/VQLVt0UdIHI', '<ul>\n	<li>Сөздік</li>\n	<li>нәтижелерім жаман емес &ndash; мои результаты неплохие</li>\n	<li>түсіне бастадым &ndash; начала понимать</li>\n	<li>қазақша бағдарламалар &ndash; передачи на казахском</li>\n	<li>көре бастадым &ndash; начала смотреть</li>\n	<li>сөздердің мағынасы &ndash; значение слов</li>\n	<li>ақырындап ұға бастадым &ndash; стала понимать понемногу</li>\n	<li>жаттай бастадым &ndash; начала учить наизусть</li>\n	<li>ондай-ондай хан қызында да болады - такое случается и у дочери хана (и на старуху бывает проруха)</li>\n	<li>бәрін түсінетін боласыз &ndash; все будете понимать</li>\n	<li>бола береді - так бывает; так случается;</li>\n	<li>Қалай аударылады? &ndash; Как переводится?</li>\n	<li>Мен өзіме сенімдімін &ndash; Я уверена в себе</li>\n	<li>Жарайсыз &ndash; Браво, молодец!</li>\n</ul>\n', '<ul>\n	<li>- Сәлем, Вера! Қалыңыз қалай?</li>\n	<li>- Бәрі ойдағыдай. Көңіл-күйім де жақсы.</li>\n	<li>- Иә, қазақша жап-жақсы сөйлей бастапсыз...</li>\n	<li>- Иә, нәтижелерім жаман емес. Адамдардың сөйлескенін де түсіне бастадым.</li>\n	<li>- Бәрін түсінесіз бе?</li>\n	<li>- Жоқ, мен әлі біраз сөздерді білмеймін ғой, әзірше аздап түсінемін.</li>\n	<li>- Сізге қазақша бағдарламаларды көбірек көру керек.</li>\n	<li>- Иә, өткен аптадан бастап жаңалықтарды қазақша көре бастадым.</li>\n	<li>- Иә, қалай екен?</li>\n	<li>- Біраз сөздердің мағынасын ақырындап түсіне бастадым, жаңа сөздерді де жаттай бастадым. Бірақ, кейде түсінбей қаламын.</li>\n	<li>- Иә, ондай-ондай хан қызында да болады. Кейін бәрін түсінетін боласыз.</li>\n	<li>- Түсінбедім, сіз не дедіңіз?</li>\n	<li>- Бастапқы кезде ондай сәттер бола береді. Бірер жылда бәрін үйреніп шығасыз. Ең бастысы, ынта-ықылас болу керек.</li>\n	<li>- Тағы да жаңа сөз үйрендім, қалай аударылады?</li>\n	<li>- Ынта-ықылас &laquo;желание, усердие, рвение&raquo; дегенді білдіреді.</li>\n	<li>- Мен өзіме сенімдімін, ынта-ықылас та бар.</li>\n	<li>- Жарайсыз, сізге жетістік тілеймін!</li>\n	<li>- Рақмет!</li>\n</ul>\n', '<ul>\n	<li>- saelem, vera! qalyngyz qalaj?</li>\n	<li>- baeri ojdaghydaj. koengil-kuejim de zhaqsy.</li>\n	<li>- ijae, qazaqsha zhap-zhaqsy soejlej bastapsyz...</li>\n	<li>- ijae, naetijzhelerim zhaman emes. adamdardyng soejleskenin de tuesine bastadym.</li>\n	<li>- baerin tuesinesiz be?</li>\n	<li>- zhoq, men aeli biraz soezderdi bilmejmin ghoj, aezirshe azdap tuesinemin.</li>\n	<li>- sizge qazaqsha baghdarlamalardy koebirek koeru kerek.</li>\n	<li>- ijae, oetken aptadan bastap zhangalyqtardy qazaqsha koere bastadym.</li>\n	<li>- ijae, qalaj eken?</li>\n	<li>- biraz soezderding maghynasyn aqyryndap tuesine bastadym, zhanga soezderdi de zhattaj bastadym. biraq, kejde tuesinbej qalamyn.</li>\n	<li>- ijae, ondaj-ondaj han qyzynda da bolady. kejin baerin tuesinetin bolasyz.</li>\n	<li>- tuesinbedim, siz ne dedingiz?</li>\n	<li>- bastapqy kezde ondaj saetter bola beredi. birer zhylda baerin uejrenip shyghasyz. eng bastysy, ynta-yqylas bolu kerek.</li>\n	<li>- taghy da zhanga soez uejrendim, qalaj audarylady?</li>\n	<li>- ynta-yqylas &laquo;zhelanije, userdije, rvenije&raquo; degendi bildiredi.</li>\n	<li>- men oezime senimdimin, ynta-yqylas ta bar.</li>\n	<li>- zharajsyz, sizge zhetistik tilejmin!</li>\n	<li>- raqmet!</li>\n</ul>\n', '7618be48-e113-472d-8fc3-2a17b10033d8'),
('d0e448c9-ae3d-478e-b0a6-352140df0e2d', 'Урок 2.1 Видеоролик и диалог', 'Сабақ 2.1 Бейне және диалог', 'Sabaq 2.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/2BfsRA4_muM', '<ul>\n	<li>- Асем, ты сегодня рано встала?</li>\n	<li>- Да, я готовлю завтрак, садись к столу.</li>\n	<li>- Спасибо. Асем, смотрю, ты с утра столько всего вкусного наготовила!</li>\n	<li>- Садись скорее к столу, пока все не остыло!</li>\n	<li>- Передай мне, пожалуйста, вилку.</li>\n	<li>- Вот, возьми.</li>\n	<li>- Спасибо. Ммм, как вкусно, я люблю домашнюю еду. Какое тесто ты ставишь на самсу?</li>\n	<li>- Обычно я делаю из слоеного теста. В перекрученное мясо добавляю тыкву и лук.</li>\n	<li>- И блинчики получились очень вкусные. Сколько яиц ты добавила ?</li>\n	<li>- Я на литр молока добавила три яйца, три стакана муки и три ложки сахара.</li>\n	<li>- Я обожаю блины, но у меня блинчики всегда получаются толстыми.</li>\n	<li>- Тесто для блинов не должно быть очень густым или слишком жидким. Главное, чтобы молоко было теплым, и дать тесту постоять.</li>\n	<li>- Спасибо, я завтра же буду готовить блины по твоему рецепту. И приглашу Вас с Томирис на чай. Асем, кстати, а где твоя дочка?</li>\n	<li>- Она рано позавтракала и убежала на занятия.</li>\n	<li>- Ладно, я пойду. Спасибо, все было очень вкусно. Передай привет Томирис.</li>\n</ul>\n', '<ul>\n	<li>- Әсем, бүгін ерте тұрғансың ба?</li>\n	<li>- Иә, ас әзірлеп жатырмын, дастарқанға кел.</li>\n	<li>- Рақмет. Әсем, таңертеңмен қаншама дәмді тағамдар әзірлегенсің?!</li>\n	<li>- Тезірек отыр, әйтпесе суып кетеді.</li>\n	<li>- Маған шанышқы алып беріп жіберші.</li>\n	<li>- Міне, ала ғой.</li>\n	<li>- Рақмет. Ммм, қандай дәмді, үйдің тамағын сондай жақсы көремін. Самсаны қандай қамырдан пісіресің?</li>\n	<li>- Көбінесе, қаттама қамырдан жасаймын. Тартылған етке асқабақ пен пияз қосамын.</li>\n	<li>- Құймағың да өте дәмді болып шығыпты. Неше жұмыртқа қостың?</li>\n	<li>- Бір литр сүтке үш жұмыртқа, үш стакан ұн, үш қасық қант қостым.</li>\n	<li>- Өз басым құймақты өте жақсы көремін. Бірақ менің құймақтарым үнемі қалың болып шығады.</li>\n	<li>- Құймақтың қамыры қатты қою да, сұйық та болмауы керек. Ең бастысы, сүтті жылытып алу керек, және қамырды аз уақытқа қойып қою керек.</li>\n	<li>- Рақмет, ертең дәл сен айтқандай жасап көрмесем... Сосын Томирис екеуіңді шайға шақырамын. Әсем, қызың қайда кеткен?</li>\n	<li>- Ол таңғы асын ертерек ішіп алып, сабағына кетті.</li>\n	<li>- Жақсы, мен кетейін. Рақмет, бәрі өте дәмді болды. Томириске сәлем айт!</li>\n</ul>\n', '<ul>\n	<li>- aesem, buegin erte turghansyng ba?</li>\n	<li>- ijae, as aezirlep zhatyrmyn, dastarqangha kel.</li>\n	<li>- raqmet. aesem, tangertengmen qanshama daemdi taghamdar aezirlegensing?!</li>\n	<li>- tezirek otyr, aejtpese suyp ketedi.</li>\n	<li>- maghan shanyshqy alyp berip zhibershi.</li>\n	<li>- mine, ala ghoj.</li>\n	<li>- raqmet. mmm, qandaj daemdi, uejding tamaghyn sondaj zhaqsy koeremin. samsany qandaj qamyrdan pisiresing?</li>\n	<li>- koebinese, qattama qamyrdan zhasajmyn. tartylghan etke asqabaq pen pijjaz qosamyn.</li>\n	<li>- qujmaghyng da oete daemdi bolyp shyghypty. neshe zhumyrtqa qostyng?</li>\n	<li>- bir lijtr suetke uesh zhumyrtqa, uesh stakan un, uesh qasyq qant qostym.</li>\n	<li>- oez basym qujmaqty oete zhaqsy koeremin. biraq mening qujmaqtarym uenemi qalyng bolyp shyghady.</li>\n	<li>- qujmaqtyng qamyry qatty qoju da, sujyq ta bolmauy kerek. eng bastysy, suetti zhylytyp alu kerek, zhaene qamyrdy az uaqytqa qojyp qoju kerek.</li>\n	<li>- raqmet, erteng dael sen ajtqandaj zhasap koermesem... sosyn tomijrijs ekeuingdi shajgha shaqyramyn. aesem, qyzyng qajda ketken?</li>\n	<li>- ol tangghy asyn erterek iship alyp, sabaghyna ketti.</li>\n	<li>- zhaqsy, men ketejin. raqmet, baeri oete daemdi boldy. tomijrijske saelem ajt!</li>\n</ul>\n', 'a79497de-6942-47bb-b8e6-df0f4594fcae'),
('a32a14d0-a0eb-49f2-a55a-4b19f599c5f4', 'Урок 3.1 Видеоролик и диалог', 'Сабақ 3.1 Бейне және диалог', 'Sabaq 3.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/uHaJTGib_3U', '<ul>\n	<li>- Здравствуй, Асхат!</li>\n	<li>- Привет, Айзере! Куда ты так спешишь?</li>\n	<li>- Я записалась на подготовительные курсы по химии.</li>\n	<li>- Решила поступить в медицинский?</li>\n	<li>- Да, так решила. А сам ты куда идешь, что несешь?</li>\n	<li>- Это мои рисунки, сделанные на компьютере с помощью специальных программ.</li>\n	<li>- Ты станешь дизайнером?</li>\n	<li>- Да, но не просто, а веб-дизайнером. Ты ведь знаешь, я уже давно этим увлекаюсь. И теперь я решил сделать его своей профессией.</li>\n	<li>- А что родители думают о твоем выборе?</li>\n	<li>- Они тоже поддерживают меня. А вот мой дедушка, который работает терапевтом, хочет, чтобы я тоже стал доктором.</li>\n	<li>- Да, сейчас некоторые студенты учатся ради родителей. Они же выбирают и ВУЗ, и специальность.</li>\n	<li>- Их тоже можно понять. Они мечтают о светлом будущем, о престижной работе для ребенка.</li>\n	<li>- Да, это тоже верно. Ладно, я пойду.</li>\n	<li>- Айзере, звони, не пропадай!</li>\n	<li>- Хорошо, как-нибудь позвоню.</li>\n</ul>\n', '<ul>\n	<li>- Сәлем, Асхат!</li>\n	<li>- Сәлем, Айзере! Қайда асығып бара жатырсың?</li>\n	<li>- Химиядан дайындық курсына жазылып едім.</li>\n	<li>- Медицинаға баратын болдың ба?</li>\n	<li>- Иә, солай шештім. Өзің қайда бара жатырсың, қолыңдағы не?</li>\n	<li>- Суреттерім ғой, арнайы компьютерлік программамен әзірленген.</li>\n	<li>- Дизайнер болғың келе ме?</li>\n	<li>- Жай дизайнер емес, веб-дизайнер болғым келеді. Өзің де білесің, мұнымен көптен бері әуестеніп жүрмін. Болашақта да осы жұмыспен айналысқым келеді.</li>\n	<li>- Ата-аналарың не дейді?</li>\n	<li>- Олар да мені қолдап отыр. Тек атам өзі терапевт болған соң менің де дәрігер болғанымды қалайды.</li>\n	<li>- Иә, қазір кейбір студенттер өз ата-анасы үшін оқып жүр. Оқу орнын да, мамандықты да ата-аналары таңдайды.</li>\n	<li>- Оларды да түсінуге болады. Олар баласының жарқын болашағын, жақсы жұмысқа тұрғанын армандайды.</li>\n	<li>- Иә, оның да дұрыс. Жақсы, мен кеттім.</li>\n	<li>- Айзере, телефон соғып тұрсаңшы.</li>\n	<li>- Жақсы, қолым тигенде телефон соғармын.</li>\n</ul>\n', '<ul>\n	<li>- saelem, ashat!</li>\n	<li>- saelem, ajzere! qajda asyghyp bara zhatyrsyng?</li>\n	<li>- hijmijjadan dajyndyq kursyna zhazylyp edim.</li>\n	<li>- medijsijnagha baratyn boldyng ba?</li>\n	<li>- ijae, solaj sheshtim. oezing qajda bara zhatyrsyng, qolyngdaghy ne?</li>\n	<li>- suretterim ghoj, arnajy kompjuterlik programmamen aezirlengen.</li>\n	<li>- dijzajner bolghyng kele me?</li>\n	<li>- zhaj dijzajner emes, veb-dijzajner bolghym keledi. oezing de bilesing, munymen koepten beri aeuestenip zhuermin. bolashaqta da osy zhumyspen ajnalysqym keledi.</li>\n	<li>- ata-analaryng ne dejdi?</li>\n	<li>- olar da meni qoldap otyr. tek atam oezi terapevt bolghan song mening de daeriger bolghanymdy qalajdy.</li>\n	<li>- ijae, qazir kejbir studentter oez ata-anasy ueshin oqyp zhuer. oqu ornyn da, mamandyqty da ata-analary tangdajdy.</li>\n	<li>- olardy da tuesinuge bolady. olar balasynyng zharqyn bolashaghyn, zhaqsy zhumysqa turghanyn armandajdy.</li>\n	<li>- ijae, onyng da durys. zhaqsy, men kettim.</li>\n	<li>- ajzere, telefon soghyp tursangshy.</li>\n	<li>- zhaqsy, qolym tijgende telefon sogharmyn.</li>\n</ul>\n', 'a9c0ef39-5ff4-4ae2-a3b1-5d0dcc649127'),
('49b7fa65-169e-43d7-a2af-addf9beaee28', 'Урок 4.1 Видеоролик и диалог', 'Сабақ 4.1 Бейне және диалог', 'Sabaq 4.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/0UW16xfyX-0', '<ul>\n	<li>- Привет, Мадина!</li>\n	<li>- Привет, Алия! Не смогла вчера до тебя дозвониться. Все хорошо?</li>\n	<li>- Да, все хорошо. А у тебя что с голосом? Ты не заболела?</li>\n	<li>&ndash; Да, кажется, я простудилась. Сейчас дома, лечусь. Что нового у тебя?</li>\n	<li>&ndash; Как раз мне есть, что тебе рассказать. Я влюбилась в одного парня&hellip;</li>\n	<li>&ndash; Когда ты успела? Мы виделись только на прошлых выходных, и у тебя не было никакой влюбленности.</li>\n	<li>&ndash; Это произошло совсем недавно. На днях у меня сломался телефон. И мне пришлось везти его на ремонт. Мастер был очень милый молодой человек. Он обещал мне починить телефон и попросил прийти на следующий день. Когда я пришла забирать телефон, он подарил мне букет роз и сказал, что я ему очень понравилась. Вчера он позвал меня на свидание, и мы провели весь вечер вдвоем. Он замечательный, умный и очень добрый. И вот, я влюбилась.</li>\n	<li>&ndash; Алия, но ты же знаешь его совсем мало времени. Как можно говорить, что любишь человека, если знакома с ним меньше недели?</li>\n	<li>- Дорогая моя, это называется любовь с первого взгляда. Ты сможешь это понять, только когда сама это ощутишь. Ой, Мадинка, можно я тебе перезвоню? Он звонит, я очень хочу ответить.</li>\n	<li>- Конечно, потом поговорим. Надеюсь, ты скоро нас познакомишь.</li>\n	<li>- Обязательно! Еще созвонимся! Выздоравливай!</li>\n	<li>&ndash; Пока.</li>\n</ul>\n', '<ul>\n	<li>- Сәлем, Мәдина!</li>\n	<li>- Сәлем, Әлия! Кеше саған телефон соғып едім, ала алмай қойдым. Бәрі жақсы ма?</li>\n	<li>- Иә, бәрі жақсы. Даусыңа не болған? Ауырып қалғансың ба?</li>\n	<li>- Ия, суық тиіп қалған сияқты. Қазір үйдемін, емделіп жатырмын. Өзіңде не жаңалық?</li>\n	<li>- Саған айтатын керемет жаңалығым бар. Мен бір жігітке ғашық болып қалдым.</li>\n	<li>- Қашан үлгеріп жүрсің? Өткен демалыста ғана кездескен жоқпыз ба? Ол кезде ғашық болғаныңды айтпап едің.</li>\n	<li>- Бұл жақында ғана болды емес пе?! Осыдан бірнеше күн бұрын телефоным бұзылып қалған. Сосын оны жөндетуіме тура келді. Телефон жөндеуші шебер жап-жақсы, жас жігіт екен. Телефонды жөндеп қоятынын айтып, келесі күні келуімді өтінді. Келесі күні барсам, бір шоқ раушан гүлін сыйлап, мені ұнатып қалғанын айтып тұр. Кеше кездесуге шақырды, кеш бойы екеуміз әңгімелесіп отырдық. Өзі сондай ашық, ақылды, әрі мейірімді екен. Мен де оны ұнатып қалдым.</li>\n	<li>- Әлия, сенің онымен танысқаныңа бір-екі күн ғана болды ғой. Бір апта өтпей жатып, қалай оны жақсы көремін дейсің?</li>\n	<li>- Құрбым-ау, бұл сезімді бір көргеннен ғашық болу дейді. Мұны өз басыңнан өткенде ғана түсінесің. Тұра тұршы, Мәдина, ол маған телефон соғып жатыр. Өзіңмен кейін хабарлассам бола ма, онымен сөйлескенше асығып тұрмын.</li>\n	<li>- Әрине, сосын сөйлесерміз. Кейін таныстыратын шығарсың?</li>\n	<li>- Міндетті түрде! Хабарласамын ғой, сауығып кет!</li>\n	<li>- Көріскенше!</li>\n</ul>\n', '<ul>\n	<li>- saelem, maedijna!</li>\n	<li>- saelem, aelijja! keshe saghan telefon soghyp edim, ala almaj qojdym. baeri zhaqsy ma?</li>\n	<li>- ijae, baeri zhaqsy. dausynga ne bolghan? auyryp qalghansyng ba?</li>\n	<li>- ijja, suyq tijip qalghan sijjaqty. qazir uejdemin, emdelip zhatyrmyn. oezingde ne zhangalyq?</li>\n	<li>- saghan ajtatyn keremet zhangalyghym bar. men bir zhigitke ghashyq bolyp qaldym.</li>\n	<li>- qashan uelgerip zhuersing? oetken demalysta ghana kezdesken zhoqpyz ba? ol kezde ghashyq bolghanyngdy ajtpap eding.</li>\n	<li>- bul zhaqynda ghana boldy emes pe?! osydan birneshe kuen buryn telefonym buzylyp qalghan. sosyn ony zhoendetuime tura keldi. telefon zhoendeushi sheber zhap-zhaqsy, zhas zhigit eken. telefondy zhoendep qojatynyn ajtyp, kelesi kueni keluimdi oetindi. kelesi kueni barsam, bir shoq raushan guelin syjlap, meni unatyp qalghanyn ajtyp tur. keshe kezdesuge shaqyrdy, kesh bojy ekeumiz aenggimelesip otyrdyq. oezi sondaj ashyq, aqyldy, aeri mejirimdi eken. men de ony unatyp qaldym.</li>\n	<li>- aelijja, sening onymen tanysqanynga bir-eki kuen ghana boldy ghoj. bir apta oetpej zhatyp, qalaj ony zhaqsy koeremin dejsing?</li>\n	<li>- qurbym-au, bul sezimdi bir koergennen ghashyq bolu dejdi. muny oez basyngnan oetkende ghana tuesinesing. tura turshy, maedijna, ol maghan telefon soghyp zhatyr. oezingmen kejin habarlassam bola ma, onymen soejleskenshe asyghyp turmyn.</li>\n	<li>- aerijne, sosyn soejlesermiz. kejin tanystyratyn shygharsyng?</li>\n	<li>- mindetti tuerde! habarlasamyn ghoj, sauyghyp ket!</li>\n	<li>- koeriskenshe!</li>\n</ul>\n', 'd6406b1d-dc51-4486-9b30-036681b83d85'),
('d5b5b488-9645-4443-bee4-fae9e6f5e7b2', 'Урок 5.1 Видеоролик и диалог', 'Сабақ 5.1 Бейне және диалог', 'Sabaq 5.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/fbpxlM5WU9k', '<ul>\n	<li>Куралай: Привет, Аскар!</li>\n	<li>Аскар: Привет, Куралай, как дела?</li>\n	<li>Куралай: Прекрасно! Я только что вернулась из спортивного клуба.</li>\n	<li>Аскар: Я не знал, что ты занимаешься спортом.</li>\n	<li>Куралай: О, да, я достаточно много занимаюсь спортом. По понедельникам и средам я хожу на аэробику. Это полезно для сердца и еще помогает мне сохранять форму.</li>\n	<li>Аскар: Это правда, ты прекрасно выглядишь! Чем еще занимаешься?</li>\n	<li>Куралай: По вторникам я хожу на йогу.</li>\n	<li>Аскар: А что с пятницей?</li>\n	<li>Куралай: Приближается лето, поэтому я стараюсь заниматься каждый день. По пятницам я хожу в тренажёрный зал и тренируюсь с гантелями в течение двух часов. Это самый хороший способ укрепления мышц. А ты сам, чем занимаешься?</li>\n	<li>Аскар: Я тоже бегаю каждый день по утрам. И упражняюсь каждый вечер перед сном.</li>\n	<li>Куралай: А почему бы тебе не пойти со мной в спортивный клуб? У тебя будет возможность потренироваться побольше.</li>\n	<li>Аскар: Ты права. Стоит попробовать. А что ещё можно делать, чтобы оставаться здоровой?</li>\n	<li>Куралай: Как ты знаешь, я не курю и не пью алкоголь. Я ем только здоровую пищу.</li>\n	<li>Аскар: Здоровую пищу?</li>\n	<li>Куралай: Да, овощи, фрукты, злаки, ржаной хлеб.</li>\n	<li>Аскар: Я тоже хочу вести здоровый образ жизни. Я буду есть только здоровую пищу. Когда мы идём в спортивый клуб?</li>\n</ul>\n', '<ul>\n	<li>Құралай: Сәлем, Асқар!</li>\n	<li>Асқар: Сәлем, Құралай, қал қалай?</li>\n	<li>Құралай: Тамаша! Қазір ғана спорт клубынан келдім.</li>\n	<li>Асқар: Мен сенің спортпен айналысатыныңды білмеппін.</li>\n	<li>Құралай: Иә, мен спортқа көп уақыт бөлемін. Әр дүйсенбі және сәрсенбі сайын аэробикаға барамын. Жүрекке де пайдалы, әрі сырт пішінімді сақтауға көмектеседі.</li>\n	<li>Асқар: Мұның рас, құлпырып кетіпсің! Тағы немен айналысасың?</li>\n	<li>Құралай: Сейсенбі сайын мен йогаға барамын.</li>\n	<li>Асқар: Ал жұма күнің қалай?</li>\n	<li>Құралай: Жаз жақындап келеді, сондықтан күнде жаттығуға тырысамын. Жұма сайын жаттығу залына барып, екі сағат бойы гантельдермен жаттығамын. Бұл бұлшық еттерді қатайтудың ең жақсы әдісі. Ал өзің ше, немен айналысасың?</li>\n	<li>Асқар: Мен де күнде таңертең жүгіремін. Кешке ұйықтар алдында жаттығу жасаймын.</li>\n	<li>Құралай: Неге менімен бірге спорт клубына бармайсың? Көбірек жаттығуға мүмкіндігің болады.</li>\n	<li>Асқар: Сенікі дұрыс. Барып көруге болады. Денсаулығың жақсы болу үшін тағы не істеуге болады?</li>\n	<li>Құралай: Өзің білесің, мен шылым шекпеймін, ішімдік ішпеймін. Тек пайдалы асты ғана жеймін.</li>\n	<li>Асқар: Пайдалы ас дейсің бе?</li>\n	<li>Құралай: Иә, көкөніс, жеміс, астық дақылдар, қара бидай наны.</li>\n	<li>Асқар: Мен де салауатты өмір салтын ұстанғым келеді. Енді тек пайдалы асты жейтін боламын. Спорт клубына қашан барамыз?</li>\n</ul>\n', '<ul>\n	<li>quralaj: saelem, asqar!</li>\n	<li>asqar: saelem, quralaj, qal qalaj?</li>\n	<li>quralaj: tamasha! qazir ghana sport klubynan keldim.</li>\n	<li>asqar: men sening sportpen ajnalysatynyngdy bilmeppin.</li>\n	<li>quralaj: ijae, men sportqa koep uaqyt boelemin. aer duejsenbi zhaene saersenbi sajyn aerobijkagha baramyn. zhuerekke de pajdaly, aeri syrt pishinimdi saqtaugha koemektesedi.</li>\n	<li>asqar: munyng ras, qulpyryp ketipsing! taghy nemen ajnalysasyng?</li>\n	<li>quralaj: sejsenbi sajyn men jogagha baramyn.</li>\n	<li>asqar: al zhuma kuening qalaj?</li>\n	<li>quralaj: zhaz zhaqyndap keledi, sondyqtan kuende zhattyghugha tyrysamyn. zhuma sajyn zhattyghu zalyna baryp, eki saghat bojy ganteldermen zhattyghamyn. bul bulshyq etterdi qatajtudyng eng zhaqsy aedisi. al oezing she, nemen ajnalysasyng?</li>\n	<li>asqar: men de kuende tangerteng zhuegiremin. keshke ujyqtar aldynda zhattyghu zhasajmyn.</li>\n	<li>quralaj: nege menimen birge sport klubyna barmajsyng? koebirek zhattyghugha muemkindiging bolady.</li>\n	<li>asqar: seniki durys. baryp koeruge bolady. densaulyghyng zhaqsy bolu ueshin taghy ne isteuge bolady?</li>\n	<li>quralaj: oezing bilesing, men shylym shekpejmin, ishimdik ishpejmin. tek pajdaly asty ghana zhejmin.</li>\n	<li>asqar: pajdaly as dejsing be?</li>\n	<li>quralaj: ijae, koekoenis, zhemis, astyq daqyldar, qara bijdaj nany.</li>\n	<li>asqar: men de salauatty oemir saltyn ustanghym keledi. endi tek pajdaly asty zhejtin bolamyn. sport klubyna qashan baramyz?</li>\n</ul>\n', '04988771-280a-41c4-830e-debc497f72ec'),
('98d1d13b-6422-49da-8fd2-36a076cd0ea5', 'Урок 1.1 Видеоролик и диалог', 'Сабақ 1.1 Бейне және диалог', 'Sabaq 1.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/xp1iBLemBzo', '<ul>\n	<li>Консультант: Здравствуйте, могу я вам чем-то помочь?</li>\n	<li>Покупатель: Добрый день, да, помогите, пожалуйста. Я хочу приобрести принтер.</li>\n	<li>&nbsp;</li>\n	<li>Консультант: У нас большой выбор принтеров. Чтобы выбрать правильный, давайте определимся с форматом документов, которые вы будете печатать.</li>\n	<li>Покупатель: Мы в основном с документами формата А4.</li>\n	<li>Консультант: Тогда вам идеально подойдёт монохромный лазерный принтер. Пойдемте, я покажу вам модели таких принтеров.</li>\n	<li>Покупатель: Подождите. Иногда мне нужно делать ксерокопии документов или бывает нужно сканировать их. А еще я слышал, что есть такие принтеры, на которых можно распечатывать и фотографии.</li>\n	<li>Консультант: Тогда покупайте МФУ.</li>\n	<li>Покупатель: МФУ?</li>\n	<li>Консультант: Многофункциональное устройство, совмещённое со сканером. И сейчас такие модели принтеров стоят недорого.</li>\n	<li>Покупатель: А сколько стоит такой принтер?</li>\n	<li>Консультант: Это зависит от выбранной вами модели и фирмы принтера. Есть и цветные лазерные принтеры, и струйные принтеры.</li>\n	<li>Покупатель: Наверное, я выберу струйный принтер с шестицветной печатью. А можно будет оформить его в кредит?</li>\n	<li>Консультант: Да, конечно.</li>\n	<li>Покупатель: Отлично, тогда беру.</li>\n	<li>Консультант: Если так, то проходите к кассе. Я принесу вам принтер туда.</li>\n</ul>\n', '<ul>\n	<li>Кеңесші: Сәлеметсіз бе, сізге менің көмегім керек пе?</li>\n	<li>Сатып алушы: Қайырлы күн, иә, маған көмектесіп жіберіңізші, принтер сатып алайын деп едім.</li>\n	<li>Кеңесші: Бізде принтерлер өте көп. Қажеттісін таңдау үшін, шығаратын құжаттарыңыздың пішімін анықтап алған жөн.</li>\n	<li>Сатып алушы: Біз негізінде А4 пішімді құжаттармен жұмыс істейміз.</li>\n	<li>Кеңесші: Ендеше сізге монохромды лазерлі принтер алу керек. Жүріңіз, мен сізге сондай принтердің үлгілерін көрсетейін.</li>\n	<li>Сатып алушы: Тұра тұрыңызшы, ал құжаттардың көшірмесін жасау, немесе сканерлеу қажет болса ше? Сосын осы суреттерді де басып шығара беретін принтер бар деп естідім.</li>\n	<li>Кеңесші: Онда КФҚ сатып алыңыз.</li>\n	<li>Сатып алушы: КФҚ деген не?</li>\n	<li>Кеңесші: Сканермен біріктірілген көп функционалды құрылғы. Қазір мұндай принтерлер қымбат та емес.</li>\n	<li>Сатып алушы: Ондай принтер қанша тұрады?</li>\n	<li>Кеңесші: Бұл өзіңіз таңдаған принтердің түрі мен фирмасына байланысты. Түрлі түсті лазерлі принтерлер де бар, бүріккіш принтерлер де бар.</li>\n	<li>Сатып алушы: Мен алты түсті бүріккіш принтерді аламын деп ойлап тұрмын. Оны несиеге сатып алуға бола ма?</li>\n	<li>Кеңесші: Әрине, болады.</li>\n	<li>Сатып алушы: Керемет, онда сатып аламын.</li>\n	<li>Кеңесші: Олай болса, сіз кассаға бара беріңіз. Мен принтерді сонда әкеліп беремін.</li>\n</ul>\n', '<ul>\n	<li>kengesshi: saelemetsiz be, sizge mening koemegim kerek pe?</li>\n	<li>satyp alushy: qajyrly kuen, ijae, maghan koemektesip zhiberingizshi, prijnter satyp alajyn dep edim.</li>\n	<li>kengesshi: bizde prijnterler oete koep. qazhettisin tangdau ueshin, shygharatyn quzhattaryngyzdyng pishimin anyqtap alghan zhoen.</li>\n	<li>satyp alushy: biz negizinde a4 pishimdi quzhattarmen zhumys istejmiz.</li>\n	<li>kengesshi: endeshe sizge monohromdy lazerli prijnter alu kerek. zhueringiz, men sizge sondaj prijnterding uelgilerin koersetejin.</li>\n	<li>satyp alushy: tura turyngyzshy, al quzhattardyng koeshirmesin zhasau, nemese skanerleu qazhet bolsa she? sosyn osy suretterdi de basyp shyghara beretin prijnter bar dep estidim.</li>\n	<li>kengesshi: onda kfq satyp alyngyz.</li>\n	<li>satyp alushy: kfq degen ne?</li>\n	<li>kengesshi: skanermen biriktirilgen koep funksijonaldy qurylghy. qazir mundaj prijnterler qymbat ta emes.</li>\n	<li>satyp alushy: ondaj prijnter qansha turady?</li>\n	<li>kengesshi: bul oezingiz tangdaghan prijnterding tueri men fijrmasyna bajlanysty. tuerli tuesti lazerli prijnterler de bar, buerikkish prijnterler de bar.</li>\n	<li>satyp alushy: men alty tuesti buerikkish prijnterdi alamyn dep ojlap turmyn. ony nesijege satyp alugha bola ma?</li>\n	<li>kengesshi: aerijne, bolady.</li>\n	<li>satyp alushy: keremet, onda satyp alamyn.</li>\n	<li>kengesshi: olaj bolsa, siz kassagha bara beringiz. men prijnterdi sonda aekelip beremin.</li>\n</ul>\n', '5462f6ae-7605-44e6-acb4-8d08bb174aed');
INSERT INTO `d_video_lesson` (`ID`, `NAME_RUS`, `NAME_KAZ`, `NAME_LAN`, `LINK`, `DESC_RUS`, `DESC_KAZ`, `DESC_LAN`, `D_LESSON_ID`) VALUES
('5b746f37-e7b3-486d-9cdc-d1ddd72c3a19', 'Урок 2.1 Видеоролик и диалог', 'Сабақ 2.1 Бейне және диалог', 'Sabaq 2.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/K6YlTj30nw4', '<ul>\n	<li>&ndash; Говорят, что экономно тратить деньги &ndash; это искусство. Но мне так и не удается освоить это искусство.</li>\n	<li>&ndash; Это правда. Как говорится &laquo;копейка рубль бережет&raquo;, нам нужно приучить себя распоряжаться малыми деньгами.</li>\n	<li>&ndash; Без денег нельзя и шагу ступить, поэтому, ты мне скажи, как же можно их сэкономить?</li>\n	<li>&ndash; Если хочешь, слушай. Первое, заведи книжечку, куда будешь записывать все свои покупки. Пометь даже 80 тенге, потраченные тобой на дорогу. В конце недели или месяца открой свою книжечку и посмотри. Это приучит тебя давать отчет самому себе.</li>\n	<li>&ndash; Выполнять этот твой совет совсем ведь не сложно.</li>\n	<li>&ndash; Во-вторых, экономь воду и электричество. Приучи себя открывать кран только тогда, когда нужно. Перед сном убедись, что свет везде выключен. А также будь аккуратен при использовании электроприборов. Например, когда ты гладишь, если зазвонил телефон, сначала выключи утюг, а затем беги к телефону.</li>\n	<li>&ndash; Да, это тоже нужно превратить в привычку.</li>\n	<li>&ndash; В-третьих, когда идешь в продуктовый магазин, сначала поешьте. С голодухи можешь купить в два раза больше, чем надо.</li>\n	<li>&ndash; Это тоже слова, к которым можно прислушаться.</li>\n	<li>&ndash; В-четвертых, научись ограничивать себя. Прежде чем купить какую-либо вещь, подумай как следует, сравни цены. Это говорит не о твоей жадности, а о грамотности. Особенно, сравнивай цены дорогих вещей. Очень возможно, что ноутбук, стоящий в одном месте 150 тысяч тенге, такая же модель в другом магазине со скидкой может стоит 100 тысяч тенге.</li>\n	<li>&ndash; Это тоже правда.</li>\n	<li>&ndash; В-пятых, остерегайся ходить в магазин в день получения зарплаты. Потому что в этот день ты можешь потратить больше чем нужно. Вот, если всегда помнить эти правила, каждый день их придерживаться, научишься экономить свой бюджет!</li>\n</ul>\n', '<ul>\n	<li>&ndash; &laquo;Ақшаны үнемдеп жарата білу &ndash; өнер&raquo;, &ndash; дейді екен ғой. Сол өнерді ешбір ұқпай&ndash;ақ қойдым.</li>\n	<li>&ndash; Рас айтасың. &laquo;Теңге тиыннан құралады&raquo; демекші, аз ақшаны ұстай білудің өзін үйрене білуіміз керек.</li>\n	<li>&ndash; Ақшасыз бір қадам аттай алмайсың, Өзің маған айтшы, оны қалай үнемдеуге болады?</li>\n	<li>&ndash; Қаласаң, құлақ қойып тыңда. Біріншіден, сатып алған кез келген затты жазып отыратын кітапша арна. Оған тіпті жолыңа кеткен 80 теңгені де түртіп қой. Аптаның не айдың соңында кітапшаңды ашып қара. Бұл өз-өзіңе есеп беруді үйретеді.</li>\n	<li>&nbsp;</li>\n	<li>&ndash; Бұл кеңесіңді орындау аса қиын емес екен.</li>\n	<li>&ndash; Екіншіден, су мен қуат көзін үнемде. Кранды керек кезде ғана ашуға үйрен. Түнде жатарда барлық жердегі жарықтың сөнгенін қадағала. Электр құралдарын пайдалағанда да мұқият бол. Мәселен, телефон үтіктеп тұрғанда шырылдаса, үтікті суырып барып, телефонды көтер.</li>\n	<li>&ndash; Иә, бұны да әдетке айналдыру керек екен.</li>\n	<li>&ndash; Үшіншіден, азық-түлік дүкеніне барғанда, үйден тойып шық. Ашкөздікпен алып қойған затың екі есе шығынға ұшыратуы мүмкін.</li>\n	<li>&ndash; Бұл да ақылға қонатын сөз екен.</li>\n	<li>&ndash; Төртіншіден, өз-өзіңе шектеу қоя біл. Кез келген затты сатып алмас бұрын, ой елегінен өткіз, бағаларын салыстыр. Бұл сенің сараңдығың емес, сауаттылығың. Әсіресе, қымбат заттардың бағаларын салыстыр. Бір жерде 150 мың теңге тұратын ноутбуктың дәл осы моделі басқа бір дүкенде жеңілдікпен 100 мың теңгеге сатылып жатуы әбден мүмкін.</li>\n	<li>&nbsp;</li>\n	<li>&ndash; Бұл айтқаның да дұрыс.</li>\n	<li>&ndash; Бесіншіден, айлық алған күні дүкенге бармауға тырыс. Өйткені ол күні, ақшаңды шектен тыс жаратып қоюың мүмкін. Міне, осы шарттарды жадыңда сақтап, күнделікті іс&ndash;әрекетке айналдырсаң, бюджетіңді үнемдеп үйренесің!</li>\n</ul>\n', '<ul>\n	<li>&ndash; &laquo;aqshany uenemdep zharata bilu &ndash; oener&raquo;, &ndash; dejdi eken ghoj. sol oenerdi eshbir uqpaj&ndash;aq qojdym.</li>\n	<li>&ndash; ras ajtasyng. &laquo;tengge tijynnan quralady&raquo; demekshi, az aqshany ustaj biluding oezin uejrene biluimiz kerek.</li>\n	<li>&ndash; aqshasyz bir qadam attaj almajsyng, oezing maghan ajtshy, ony qalaj uenemdeuge bolady?</li>\n	<li>&ndash; qalasang, qulaq qojyp tyngda. birinshiden, satyp alghan kez kelgen zatty zhazyp otyratyn kitapsha arna. oghan tipti zholynga ketken 80 tenggeni de tuertip qoj. aptanyng ne ajdyng songynda kitapshangdy ashyp qara. bul oez-oezinge esep berudi uejretedi.</li>\n	<li>&ndash; bul kengesingdi oryndau asa qijyn emes eken.</li>\n	<li>&ndash; ekinshiden, su men quat koezin uenemde. krandy kerek kezde ghana ashugha uejren. tuende zhatarda barlyq zherdegi zharyqtyng soengenin qadaghala. elektr quraldaryn pajdalaghanda da muqijjat bol. maeselen, telefon uetiktep turghanda shyryldasa, uetikti suyryp baryp, telefondy koeter.</li>\n	<li>&ndash; ijae, buny da aedetke ajnaldyru kerek eken.</li>\n	<li>&ndash; ueshinshiden, azyq-tuelik duekenine barghanda, uejden tojyp shyq. ashkoezdikpen alyp qojghan zatyng eki ese shyghyngha ushyratuy muemkin.</li>\n	<li>&ndash; bul da aqylgha qonatyn soez eken.</li>\n	<li>&ndash; toertinshiden, oez-oezinge shekteu qoja bil. kez kelgen zatty satyp almas buryn, oj eleginen oetkiz, baghalaryn salystyr. bul sening sarangdyghyng emes, sauattylyghyng. aesirese, qymbat zattardyng baghalaryn salystyr. bir zherde 150 myng tengge turatyn noutbuktyng dael osy modeli basqa bir duekende zhengildikpen 100 myng tenggege satylyp zhatuy aebden muemkin.</li>\n	<li>&ndash; bul ajtqanyng da durys.</li>\n	<li>&ndash; besinshiden, ajlyq alghan kueni duekenge barmaugha tyrys. oejtkeni ol kueni, aqshangdy shekten tys zharatyp qojuyng muemkin. mine, osy sharttardy zhadyngda saqtap, kuendelikti is&ndash;aereketke ajnaldyrsang, bjudzhetingdi uenemdep uejrenesing!</li>\n</ul>\n', 'ee5b9285-e103-4bec-b7c3-35644c6b540a'),
('9e64e952-b83f-4ce9-a862-635dbbebb493', 'Урок 3.1 Видеоролик и диалог', 'Сабақ 3.1 Бейне және диалог', 'Sabaq 3.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/Vb9VBfM0j2s', '<ul>\n	<li>зәресі ұшу &ndash; сильно испугаться</li>\n	<li>аяқтың ұшымен жүру &ndash; ходить на цыпочках</li>\n	<li>жоқ жерден &ndash; из ничего, на пустом месте</li>\n	<li>&nbsp;</li>\n	<li>Бір күн бие, бір күн түйе &ndash; так говорят о ком-либо, у кого быстро меняется настроение, решение и/или намерение.</li>\n	<li>ойламаған жерден &ndash; неожиданно, вдруг</li>\n	<li>Сол да жаңалық па? &ndash; Это разве новость?</li>\n	<li>Басында бәрі керемет болған - поначалу все было отлично.</li>\n	<li>дым болмағандай - как ни в чем не бывало</li>\n	<li>бас изеу &ndash; кивнуть</li>\n	<li>ашуға булығу &ndash; едва сдерживать гнев</li>\n	<li>&nbsp;</li>\n	<li>сырын жайып салу &ndash; рассказать все, ничего не скрывая</li>\n	<li>өлердегі сөзін айту &ndash; сильно просить</li>\n	<li>уақыт көрсетеді &ndash; время покажет</li>\n	<li>тағы бір мүмкіндік беру &ndash; давать второй шанс</li>\n</ul>\n', '<ul>\n	<li>- Сәлем, Айша!</li>\n	<li>- Тсс, ақырын, зәремді ұшырдың ғой...</li>\n	<li>- Не болды соншама?</li>\n	<li>- Бүгін бастығымның көңіл-күйі жоқ. Бәріміз аяғымыздың ұшымен жүрміз.</li>\n	<li>- Не болып қалды тағы?</li>\n	<li>- Білмеймін, таңертеңнен бері ашулы.</li>\n	<li>- Сенің бастығың өзі &laquo;бір күн бие, бір күн түйе&raquo; ғой. Сол да жаңалық па? Иә, кеше туған күн қалай өтті?</li>\n	<li>- Басында бәрі керемет болған.</li>\n	<li>- Сосын?</li>\n	<li>- Сосын ойламаған жерден Нұрлан келді.</li>\n	<li>- Ол сені көрді ме?</li>\n	<li>- Иә, дым болмағандай амандасты.</li>\n	<li>- Ал сен не істедің?</li>\n	<li>- Мен не істейін... Бас изедім де, ас үйге өтіп кеттім.</li>\n	<li>- Ол ше?</li>\n	<li>- Ол Серікті құшақтап, туған күнімен құттықтады да диванға барып отырды.</li>\n	<li>- Өзің де кеш бойы ас үйде отырмаған шығарсың?</li>\n	<li>- Жоқ, біраз уақыттан соң Нұрлан жаныма келіп, кешірім сұрады.</li>\n	<li>- Сен не дедің?</li>\n	<li>- Ашуға булығып, жылап жібердім. Барлық ренішімді, сырымды жайып салдым, кешірмейтінімді айттым. &laquo;Түсінші, кешірші. Енді мұндай жағдай қайталанбайды&raquo;, - деп өлердегі сөзін айтты.</li>\n	<li>- Енді не істемек ойың бар?</li>\n	<li>- Уақыт көрсетер, білмеймін.</li>\n	<li>- Мүмкін, оған тағы бір мүмкіндік берерсің...</li>\n</ul>\n', '<ul>\n	<li>- saelem, ajsha!</li>\n	<li>- tss, aqyryn, zaeremdi ushyrdyng ghoj...</li>\n	<li>- ne boldy sonshama?</li>\n	<li>- buegin bastyghymnyng koengil-kueji zhoq. baerimiz ajaghymyzdyng ushymen zhuermiz.</li>\n	<li>- ne bolyp qaldy taghy?</li>\n	<li>- bilmejmin, tangertengnen beri ashuly.</li>\n	<li>- sening bastyghyng oezi &laquo;bir kuen bije, bir kuen tueje&raquo; ghoj. sol da zhangalyq pa? ijae, keshe tughan kuen qalaj oetti?</li>\n	<li>- basynda baeri keremet bolghan.</li>\n	<li>- sosyn?</li>\n	<li>- sosyn ojlamaghan zherden nurlan keldi.</li>\n	<li>- ol seni koerdi me?</li>\n	<li>- ijae, dym bolmaghandaj amandasty.</li>\n	<li>- al sen ne isteding?</li>\n	<li>- men ne istejin... bas ijzedim de, as uejge oetip kettim.</li>\n	<li>- ol she?</li>\n	<li>- ol serikti qushaqtap, tughan kuenimen quttyqtady da dijvangha baryp otyrdy.</li>\n	<li>- oezing de kesh bojy as uejde otyrmaghan shygharsyng?</li>\n	<li>- zhoq, biraz uaqyttan song nurlan zhanyma kelip, keshirim surady.</li>\n	<li>- sen ne deding?</li>\n	<li>- ashugha bulyghyp, zhylap zhiberdim. barlyq renishimdi, syrymdy zhajyp saldym, keshirmejtinimdi ajttym. &laquo;tuesinshi, keshirshi. endi mundaj zhaghdaj qajtalanbajdy&raquo;, - dep oelerdegi soezin ajtty.</li>\n	<li>- endi ne istemek ojyng bar?</li>\n	<li>- uaqyt koerseter, bilmejmin.</li>\n	<li>- muemkin, oghan taghy bir muemkindik berersing...</li>\n</ul>\n', '0c546ee3-7896-4a56-b6c2-820c5b0bff3e'),
('6cb89bae-0627-4a47-ba98-207fc2223b13', 'Урок 4.1 Видеоролик и диалог', 'Сабақ 4.1 Бейне және диалог', 'Sabaq 4.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/cUFxujY4SWA', '<ul>\n	<li>Ануар едет в гости к своему другу Галыму. Галым объясняет ему по телефону, как ехать.</li>\n	<li>&nbsp;</li>\n	<li>- Привет, Ануар! Где ты?</li>\n	<li>- На углу Фурманова и Абая.</li>\n	<li>&nbsp;</li>\n	<li>- Тогда подожди меня там.Через десять минут я там буду.</li>\n	<li>- Не нужно.Я на машине, скажи мне, как ехать, и я сам найду дорогу к твоему дому.</li>\n	<li>&nbsp;</li>\n	<li>- Ладно. Поезжай вверх по Абая до улицы Достык. На перекрестке сворачиваешь налево. Продолжай ехать по Достык до улицы Казыбек би. Поверни затем направо и там увидишь цветочный магазин. Я буду тебя ждать возле этого магазина. Понял?</li>\n	<li>&nbsp;</li>\n	<li>- Да, вроде бы.</li>\n	<li>- Смотри не заблудись! Помнишь, когда в прошлый раз ты к нам приезжал на старую квартиру, ты чуть не заблудился...</li>\n	<li>- Да, тогда я действительно чуть не заблудился. Конечно, на окраине города, было еще темно, еле нашел.</li>\n	<li>- Надеюсь в этот раз не заблудишься?!</li>\n	<li>- Не бойся, найду, через пятнадцать минут буду у вас.</li>\n</ul>\n', '<ul>\n	<li>Әнуар өзінің досы Ғалымның үйіне қонаққа келе жатыр. Ғалым үйіне қалай келу керектігін телефонмен түсіндіріп жатыр.</li>\n	<li>&nbsp;</li>\n	<li>- Сәлем, Әнуар? Қайдасың?</li>\n	<li>- Фурманов пен Абай көшелерінің қиылысында тұрмын.</li>\n	<li>- Ендеше сол жерде күте тұр. Он минуттан соң боламын.</li>\n	<li>- Қажеті жоқ. Мен машинамен келе жатырмын, жолды түсіндірсең болды, өзім тауып аламын.</li>\n	<li>- Жарайды. Абай көшесімен Достыққа қарай жоғары көтерілесің. Қиылысқа келгенде солға бұрыласың. Сосын Достықпен Қазыбек би көшесіне дейін келесің. Сол көшеден оңға бұрыласың, алдыңнан гүл сататын дүкен шығады. Сені сол жерде күтіп тұрамын. Түсіндің бе?</li>\n	<li>- Иә, түсінген сияқтымын.</li>\n	<li>- Байқа, адасып кетпе! Есіңде ме, өткен жолы біздің бұрынғы үйімізге келерде адасып кете жаздап едің ғой...</li>\n	<li>- Иә, ол кезде шынымен адасып кете жаздадым. Әрине, қаланың шеті, әрі қараңғы, әзер дегенде таптым.</li>\n	<li>- Енді осы жолы адаспайтын боларсың!?</li>\n	<li>- Қорықпа, табамын, он бес минутта жетемін.</li>\n</ul>\n', '<ul>\n	<li>aenuar oezining dosy ghalymnyng uejine qonaqqa kele zhatyr. ghalym uejine qalaj kelu kerektigin telefonmen tuesindirip zhatyr.</li>\n	<li>- saelem, aenuar? qajdasyng?</li>\n	<li>- furmanov pen abaj koeshelerining qijylysynda turmyn.</li>\n	<li>- endeshe sol zherde kuete tur. on mijnuttan song bolamyn.</li>\n	<li>- qazheti zhoq. men mashijnamen kele zhatyrmyn, zholdy tuesindirseng boldy, oezim tauyp alamyn.</li>\n	<li>- zharajdy. abaj koeshesimen dostyqqa qaraj zhoghary koeterilesing. qijylysqa kelgende solgha burylasyng. sosyn dostyqpen qazybek bij koeshesine dejin kelesing. sol koesheden onggha burylasyng, aldyngnan guel satatyn dueken shyghady. seni sol zherde kuetip turamyn. tuesinding be?</li>\n	<li>- ijae, tuesingen sijjaqtymyn.</li>\n	<li>- bajqa, adasyp ketpe! esingde me, oetken zholy bizding burynghy uejimizge kelerde adasyp kete zhazdap eding ghoj...</li>\n	<li>- ijae, ol kezde shynymen adasyp kete zhazdadym. aerijne, qalanyng sheti, aeri qarangghy, aezer degende taptym.</li>\n	<li>- endi osy zholy adaspajtyn bolarsyng!?</li>\n	<li>- qoryqpa, tabamyn, on bes mijnutta zhetemin.</li>\n</ul>\n', '8d4d0204-eb72-4ba3-830b-a021deae707d'),
('dca827ad-f781-4855-8cb7-2b90f294f575', 'Урок 5.1 Видеоролик и диалог', 'Сабақ 5.1 Бейне және диалог', 'Sabaq 5.1 Bejne zhaene dijalog', 'https://www.youtube.com/embed/tl_0Xljy9qM', '<ul>\n	<li>Консультант: Здравствуйте, могу я вам чем-то помочь?</li>\n	<li>Покупатель: Добрый день, да, помогите, пожалуйста. Я хочу приобрести принтер.</li>\n	<li>Консультант: У нас большой выбор принтеров. Чтобы выбрать правильный, давайте определимся с форматом документов, которые вы будете печатать.</li>\n	<li>Покупатель: Мы в основном с документами формата А4.</li>\n	<li>Консультант: Тогда вам идеально подойдёт монохромный лазерный принтер. Пойдемте, я покажу вам модели таких принтеров.</li>\n	<li>Покупатель: Подождите. Иногда мне нужно делать ксерокопии документов или бывает нужно сканировать их. А еще я слышал, что есть такие принтеры, на которых можно распечатывать и фотографии.</li>\n	<li>Консультант: Тогда покупайте МФУ.</li>\n	<li>Покупатель: МФУ?</li>\n	<li>Консультант: Многофункциональное устройство, совмещённое со сканером. И сейчас такие модели принтеров стоят недорого.</li>\n	<li>Покупатель: А сколько стоит такой принтер?</li>\n	<li>Консультант: Это зависит от выбранной вами модели и фирмы принтера. Есть и цветные лазерные принтеры, и струйные принтеры.</li>\n	<li>Покупатель: Наверное, я выберу струйный принтер с шестицветной печатью. А можно будет оформить его в кредит?</li>\n	<li>Консультант: Да, конечно.</li>\n	<li>Покупатель: Отлично, тогда беру.</li>\n	<li>Консультант: Если так, то проходите к кассе. Я принесу вам принтер туда.</li>\n</ul>\n', '<ul>\n	<li>Кеңесші: Сәлеметсіз бе, сізге менің көмегім керек пе?</li>\n	<li>Сатып алушы: Қайырлы күн, иә, маған көмектесіп жіберіңізші, принтер сатып алайын деп едім.</li>\n	<li>Кеңесші: Бізде принтерлер өте көп. Қажеттісін таңдау үшін, шығаратын құжаттарыңыздың пішімін анықтап алған жөн.</li>\n	<li>Сатып алушы: Біз негізінде А4 пішімді құжаттармен жұмыс істейміз.</li>\n	<li>Кеңесші: Ендеше сізге монохромды лазерлі принтер алу керек. Жүріңіз, мен сізге сондай принтердің үлгілерін көрсетейін.</li>\n	<li>Сатып алушы: Тұра тұрыңызшы, ал құжаттардың көшірмесін жасау, немесе сканерлеу қажет болса ше? Сосын осы суреттерді де басып шығара беретін принтер бар деп естідім.</li>\n	<li>Кеңесші: Онда КФҚ сатып алыңыз.</li>\n	<li>Сатып алушы: КФҚ деген не?</li>\n	<li>Кеңесші: Сканермен біріктірілген көп функционалды құрылғы. Қазір мұндай принтерлер қымбат та емес.</li>\n	<li>Сатып алушы: Ондай принтер қанша тұрады?</li>\n	<li>Кеңесші: Бұл өзіңіз таңдаған принтердің түрі мен фирмасына байланысты. Түрлі түсті лазерлі принтерлер де бар, бүріккіш принтерлер де бар.</li>\n	<li>Сатып алушы: Мен алты түсті бүріккіш принтерді аламын деп ойлап тұрмын. Оны несиеге сатып алуға бола ма?</li>\n	<li>Кеңесші: Әрине, болады.</li>\n	<li>Сатып алушы: Керемет, онда сатып аламын.</li>\n	<li>Кеңесші: Олай болса, сіз кассаға бара беріңіз. Мен принтерді сонда әкеліп беремін.</li>\n</ul>\n', '<ul>\n	<li>kengesshi: saelemetsiz be, sizge mening koemegim kerek pe?</li>\n	<li>satyp alushy: qajyrly kuen, ijae, maghan koemektesip zhiberingizshi, prijnter satyp alajyn dep edim.</li>\n	<li>kengesshi: bizde prijnterler oete koep. qazhettisin tangdau ueshin, shygharatyn quzhattaryngyzdyng pishimin anyqtap alghan zhoen.</li>\n	<li>satyp alushy: biz negizinde a4 pishimdi quzhattarmen zhumys istejmiz.</li>\n	<li>kengesshi: endeshe sizge monohromdy lazerli prijnter alu kerek. zhueringiz, men sizge sondaj prijnterding uelgilerin koersetejin.</li>\n	<li>satyp alushy: tura turyngyzshy, al quzhattardyng koeshirmesin zhasau, nemese skanerleu qazhet bolsa she? sosyn osy suretterdi de basyp shyghara beretin prijnter bar dep estidim.</li>\n	<li>kengesshi: onda kfq satyp alyngyz.</li>\n	<li>satyp alushy: kfq degen ne?</li>\n	<li>kengesshi: skanermen biriktirilgen koep funksijonaldy qurylghy. qazir mundaj prijnterler qymbat ta emes.</li>\n	<li>satyp alushy: ondaj prijnter qansha turady?</li>\n	<li>kengesshi: bul oezingiz tangdaghan prijnterding tueri men fijrmasyna bajlanysty. tuerli tuesti lazerli prijnterler de bar, buerikkish prijnterler de bar.</li>\n	<li>satyp alushy: men alty tuesti buerikkish prijnterdi alamyn dep ojlap turmyn. ony nesijege satyp alugha bola ma?</li>\n	<li>kengesshi: aerijne, bolady.</li>\n	<li>satyp alushy: keremet, onda satyp alamyn.</li>\n	<li>kengesshi: olaj bolsa, siz kassagha bara beringiz. men prijnterdi sonda aekelip beremin.</li>\n</ul>\n', 'dd56f8ba-0c5a-4413-922f-de9b10a0023d'),
('e98949fe-34ba-42d5-b84f-afc9691a618a', 'Урок 1.1 Видеоролик и диалог', 'Сабақ 1.1 Бейне және диалог', 'Sabaq 1.1 Bejne zhaene dijalog', '', '<ul>\n	<li>ерекше мезгіл &ndash; особое время</li>\n	<li>биші қайың деп жырға қосқан &ndash; воспевали как &laquo;танцующие березы&raquo; шынында да &ndash; по правде говоря</li>\n	<li>қайың шырыны &ndash; берёзовый сок</li>\n	<li>емдік тұнба &ndash; лечебный настой</li>\n	<li>қалай дейтінін білмеймін &ndash; не знаю, как называется</li>\n	<li>тынығып алу &ndash; отдыхать</li>\n</ul>\n', '<ul>\n	<li>Шипажайда. Тұрғынбек ата мен оның немересі Әлихан</li>\n	<li>--------------------------------------------</li>\n	<li>- Ата, айнала қандай әдемі! Мен көктемді сондай жақсы көремін.</li>\n	<li>- Көктем &ndash; ерекше мезгіл, балам. Бұл &ndash; табиғаттың, адамзаттың жаңаратын, жасаратын кезі.</li>\n	<li>- Әлихан, сен мына ағаштың қазақша қалай аталатынын білесің бе?</li>\n	<li>- Жоқ, білмеймін ата.</li>\n	<li>- Бұл ағашты жөке дейді. Бұл &ndash; көп жылдық ағаш. Көктемде ерте бүршік жаратын ағаштардың бірі &ndash; осы жөке. Ол ерте гүлдейді, оның гүлінен әртүрлі емдік тұнбалар жасап, қолдануға болады.</li>\n	<li>- Ал мына ағаш қалай деп аталады, ата?</li>\n	<li>- Сен &laquo;қайың шырыны&raquo; деген шырынды ішіп көрдің ғой? Міне, мына ағаш &ndash; сол қайың. Қазақ ақындары қайыңды сұлу қайың, ақ қайың, биші қайың деп жырға қосқан.</li>\n	<li>- Иә, шынында да мен осы жердегі көп ағаштар мен гүлдерді қазақша қалай дейтінін білмеймін.</li>\n	<li>- Ал мына ағаштың қазақша аты &ndash; мамыргүл. Орысша оны &laquo;сирень&raquo; деп атайды. Ол &ndash; көктемде ерте гүлдейтін ағаштардың бірі.</li>\n	<li>- Ал мына ағаш &laquo;Таволга&raquo; емес пе?</li>\n	<li>- Иә, мұны қазақша &laquo;тобылғы&raquo; дейді. Одан қамшы жасауға болады. Ондай қамшыны &laquo;тобылғы сапты қамшы&raquo; деп атайды.</li>\n	<li>- Ата, осы көкек неге &laquo;кө-кек, кө-кек&raquo; деп өзінің атын айтады?</li>\n	<li>- Бұл жайында халық арасында аңыз-әңгіме көп, Әлихан. Мен саған оны кейін асықпай айтып берейін.</li>\n	<li>- Шаршадыңыз ба, ата? Бөлмеге барайық.</li>\n	<li>- Жоқ, осы жерде отырып, біраз тынығып алайық.</li>\n</ul>\n', '<ul>\n	<li>shijpazhajda. turghynbek ata men onyng nemeresi aelijhan</li>\n	<li>--------------------------------------------</li>\n	<li>- ata, ajnala qandaj aedemi! men koektemdi sondaj zhaqsy koeremin.</li>\n	<li>- koektem &ndash; erekshe mezgil, balam. bul &ndash; tabijghattyng, adamzattyng zhangaratyn, zhasaratyn kezi.</li>\n	<li>- aelijhan, sen myna aghashtyng qazaqsha qalaj atalatynyn bilesing be?</li>\n	<li>- zhoq, bilmejmin ata.</li>\n	<li>- bul aghashty zhoeke dejdi. bul &ndash; koep zhyldyq aghash. koektemde erte buershik zharatyn aghashtardyng biri &ndash; osy zhoeke. ol erte gueldejdi, onyng guelinen aertuerli emdik tunbalar zhasap, qoldanugha bolady.</li>\n	<li>- al myna aghash qalaj dep atalady, ata?</li>\n	<li>- sen &laquo;qajyng shyryny&raquo; degen shyryndy iship koerding ghoj? mine, myna aghash &ndash; sol qajyng. qazaq aqyndary qajyngdy sulu qajyng, aq qajyng, bijshi qajyng dep zhyrgha qosqan.</li>\n	<li>- ijae, shynynda da men osy zherdegi koep aghashtar men guelderdi qazaqsha qalaj dejtinin bilmejmin.</li>\n	<li>- al myna aghashtyng qazaqsha aty &ndash; mamyrguel. oryssha ony &laquo;sijren&raquo; dep atajdy. ol &ndash; koektemde erte gueldejtin aghashtardyng biri.</li>\n	<li>- al myna aghash &laquo;tavolga&raquo; emes pe?</li>\n	<li>- ijae, muny qazaqsha &laquo;tobylghy&raquo; dejdi. odan qamshy zhasaugha bolady. ondaj qamshyny &laquo;tobylghy sapty qamshy&raquo; dep atajdy.</li>\n	<li>- ata, osy koekek nege &laquo;koe-kek, koe-kek&raquo; dep oezining atyn ajtady?</li>\n	<li>- bul zhajynda halyq arasynda angyz-aenggime koep, aelijhan. men saghan ony kejin asyqpaj ajtyp berejin.</li>\n	<li>- sharshadyngyz ba, ata? boelmege barajyq.</li>\n	<li>- zhoq, osy zherde otyryp, biraz tynyghyp alajyq.</li>\n</ul>\n', '665e9f49-9178-4726-87f7-0a592e17da5a'),
('acef5295-ea9f-4107-b28d-63258c818c31', 'Урок 2.1 Видеоролик и диалог', 'Сабақ 2.1 Бейне және диалог', 'Sabaq 2.1 Bejne zhaene dijalog', '', '<ul>\n	<li>шляпаның жиегі &ndash; поля шляпы</li>\n	<li>қайырмалы шляпа &ndash; шляпа с полями</li>\n	<li>осы маусымдағы ең сәнді шляпа - самая модная шляпа в этом сезоне</li>\n	<li>өрнектері қандай әдемі &ndash; узоры такие красивые</li>\n</ul>\n', '<ul>\n	<li>- Маған ана қара шляпаны көрсетіңізші.</li>\n	<li>- Киіп көресіз бе?</li>\n	<li>- Иә. Ой, мынау маған үлкен екен. Осыдан кішірегі жоқ па?</li>\n	<li>- Ендеше, мына шляпаны киіп көріңіз.</li>\n	<li>- Мына шляпаның жиегі тым үлкен екен. Ана қызылын көрсетіңізші?!</li>\n	<li>- Бұл да қайырмалы шляпа, дегенмен қарап көріңіз.</li>\n	<li>- Формасы жап-жақсы, бірақ қып-қызыл екен...</li>\n	<li>- Сізге жарасып тұр.</li>\n	<li>- Өзі қанша тұрады?</li>\n	<li>- Отыз мың теңге.</li>\n	<li>- Басқаларға қарағанда қымбаттау екен.</li>\n	<li>- Жасыл түске қалай қарайсыз?</li>\n	<li>- Жоқ, жасыл түс маған ұнамайды. Ана жоғары сөреде тұрған сұр шляпаны алып беріңізші.</li>\n	<li>- Бұл осы маусымдағы ең сәнді шляпа, бағасы да қолайлы.</li>\n	<li>- Бұл басымды аздап қысады екен, үлкенірегі жоқ па?</li>\n	<li>- Өкінішке қарай, бұл үлгіден біреу ғана қалды.</li>\n	<li>- Бәрін киіп көрдік пе? Тағы қандай түрлері бар?</li>\n	<li>- Иә, барлық үлгілерді киіп көрдіңіз. Мүмкін орамал тартып көресіз, мына түс қалай?</li>\n	<li>- Керемет, өрнектері қандай әдемі! Неге бірден осы орамалды көрсетпедіңіз?!</li>\n	<li>- Сіз алдымен шляпа сұрадыңыз ғой... Орамалды аласыз ба?</li>\n	<li>- Әрине, аламын.</li>\n</ul>\n', '<ul>\n	<li>- maghan ana qara shljapany koersetingizshi.</li>\n	<li>- kijip koeresiz be?</li>\n	<li>- ijae. oj, mynau maghan uelken eken. osydan kishiregi zhoq pa?</li>\n	<li>- endeshe, myna shljapany kijip koeringiz.</li>\n	<li>- myna shljapanyng zhijegi tym uelken eken. ana qyzylyn koersetingizshi?!</li>\n	<li>- bul da qajyrmaly shljapa, degenmen qarap koeringiz.</li>\n	<li>- formasy zhap-zhaqsy, biraq qyp-qyzyl eken...</li>\n	<li>- sizge zharasyp tur.</li>\n	<li>- oezi qansha turady?</li>\n	<li>- otyz myng tengge.</li>\n	<li>- basqalargha qaraghanda qymbattau eken.</li>\n	<li>- zhasyl tueske qalaj qarajsyz?</li>\n	<li>- zhoq, zhasyl tues maghan unamajdy. ana zhoghary soerede turghan sur shljapany alyp beringizshi.</li>\n	<li>- bul osy mausymdaghy eng saendi shljapa, baghasy da qolajly.</li>\n	<li>- bul basymdy azdap qysady eken, uelkeniregi zhoq pa?</li>\n	<li>- oekinishke qaraj, bul uelgiden bireu ghana qaldy.</li>\n	<li>- baerin kijip koerdik pe? taghy qandaj tuerleri bar?</li>\n	<li>- ijae, barlyq uelgilerdi kijip koerdingiz. muemkin oramal tartyp koeresiz, myna tues qalaj?</li>\n	<li>- keremet, oernekteri qandaj aedemi! nege birden osy oramaldy koersetpedingiz?!</li>\n	<li>- siz aldymen shljapa suradyngyz ghoj... oramaldy alasyz ba?</li>\n	<li>- aerijne, alamyn.</li>\n</ul>\n', '0c1c426a-bd4b-4593-841e-5172850a55a0'),
('8fc3649a-2bb2-4fa0-aace-d512e329fc43', 'Урок 3.1 Видеоролик и диалог', 'Сабақ 3.1 Бейне және диалог', 'Sabaq 3.1 Bejne zhaene dijalog', '', '', '<ul>\n	<li>Дариға Қайрат Жолдасбайұлының үйіне телефон соқты.</li>\n	<li>&nbsp;</li>\n	<li>Дариға:- Сәламатсыз ба, Нәзипа жеңеше! Хал- жағдайыңыз қалай? Сізбен хабарласпағалы көп болды. Кеше телефон соғайын деп едім, қолым тимеді.</li>\n	<li>Нәзипа: - Менің де уақытым болмай жүр. Қазір Қайрат ағаң Астанада жүр. Олардың мекемесі жаңа тұрғын үйлер салып жатыр. Біз де сол жаққа көшетін шығармыз.</li>\n	<li>Дариға:- Ол жақта үйлеріңіз бар ма?</li>\n	<li>Нәзипа: - Әрине, жоқ. Қайрат ағаң бізді көшіріп алмақ. Астананың шет жағынан жекеменшік үй тұрғызайын деп жүр.</li>\n	<li>Дариға:- Үйді сатып алған оңай болмас па екен?</li>\n	<li>Нәзипа: - Астанада үйлер өте қымбат екен. Ал өзімізге ұнайтын жайлы пәтер сатып алуға қаражатымыз аздау болып тұр. Оның үстіне таяуда бізде қуанышты жаңалық болмақшы.</li>\n	<li>Дариға:- Ол қандай жаңалық?</li>\n	<li>Нәзипа: - Біздің ортаншы ұлымыз Еркебұлан үйленбекші.</li>\n	<li>Дариға:- Құтты болсын, Нәзипа жеңеше! Той қашан болады? Қалындық қай жақтың баласы?</li>\n	<li>Нәзипа: - Тойдың болатын мерзімі әлі белгісіз. Болашақ келін Тараз қаласынан. Ата-анасы мен бауырлары да Таразда тұрады екен. Енді баланың үйлену тойы үшін қамданып жатырмыз.</li>\n	<li>Дариға:- Қуанышыңыз құтты болсын, жеңеше!</li>\n	<li>Нәзипа: - Рақмет, қарағым. Өздеріңнің жағдайларың қалай? Қуаныштың жұмысы жақсы ма?</li>\n	<li>Дариға:- Иә, рақмет, жақсымыз. Қуаныш қазір батыс облыстарды аралап жүр. Бірқатар облыс орталықтарында жаңа дәріханалар ашайын деп жатыр.</li>\n	<li>Нәзипа: - Жақсы, жақсы. Талабы оң болсын. Бізден сәлем айт.</li>\n	<li>Дариға:- Жақсы, айтамын. Бізден көмек қажет болса, хабарласыңыз.</li>\n	<li>Нәзипа: - Той уақыты жақындағанда хабарласамын ғой. Сау бол, Дариға.</li>\n	<li>Дариға:- Сау болыңыз.</li>\n</ul>\n', '<ul>\n	<li>darijgha qajrat zholdasbajulynyng uejine telefon soqty.</li>\n	<li>darijgha:- saelamatsyz ba, naezijpa zhengeshe! hal- zhaghdajyngyz qalaj? sizben habarlaspaghaly koep boldy. keshe telefon soghajyn dep edim, qolym tijmedi.</li>\n	<li>naezijpa: - mening de uaqytym bolmaj zhuer. qazir qajrat aghang astanada zhuer. olardyng mekemesi zhanga turghyn uejler salyp zhatyr. biz de sol zhaqqa koeshetin shygharmyz.</li>\n	<li>darijgha:- ol zhaqta uejleringiz bar ma?</li>\n	<li>naezijpa: - aerijne, zhoq. qajrat aghang bizdi koeshirip almaq. astananyng shet zhaghynan zhekemenshik uej turghyzajyn dep zhuer.</li>\n	<li>darijgha:- uejdi satyp alghan ongaj bolmas pa eken?</li>\n	<li>naezijpa: - astanada uejler oete qymbat eken. al oezimizge unajtyn zhajly paeter satyp alugha qarazhatymyz azdau bolyp tur. onyng uestine tajauda bizde quanyshty zhangalyq bolmaqshy.</li>\n	<li>darijgha:- ol qandaj zhangalyq?</li>\n	<li>naezijpa: - bizding ortanshy ulymyz erkebulan uejlenbekshi.</li>\n	<li>darijgha:- qutty bolsyn, naezijpa zhengeshe! toj qashan bolady? qalyndyq qaj zhaqtyng balasy?</li>\n	<li>naezijpa: - tojdyng bolatyn merzimi aeli belgisiz. bolashaq kelin taraz qalasynan. ata-anasy men bauyrlary da tarazda turady eken. endi balanyng uejlenu tojy ueshin qamdanyp zhatyrmyz.</li>\n	<li>darijgha:- quanyshyngyz qutty bolsyn, zhengeshe!</li>\n	<li>naezijpa: - raqmet, qaraghym. oezderingning zhaghdajlaryng qalaj? quanyshtyng zhumysy zhaqsy ma?</li>\n	<li>darijgha:- ijae, raqmet, zhaqsymyz. quanysh qazir batys oblystardy aralap zhuer. birqatar oblys ortalyqtarynda zhanga daerihanalar ashajyn dep zhatyr.</li>\n	<li>naezijpa: - zhaqsy, zhaqsy. talaby ong bolsyn. bizden saelem ajt.</li>\n	<li>darijgha:- zhaqsy, ajtamyn. bizden koemek qazhet bolsa, habarlasyngyz.</li>\n	<li>naezijpa: - toj uaqyty zhaqyndaghanda habarlasamyn ghoj. sau bol, darijgha.</li>\n	<li>darijgha:- sau bolyngyz.</li>\n</ul>\n', 'c4df7795-a623-41fb-970e-1bb1c8e27d09');
